import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';

const AppContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem;
  text-align: center;
  background-color: #2b2b2b;
  min-height: 100vh;
  color: #fff;
`;

const Title = styled.h1`
  color: #fff;
  margin-bottom: 2rem;
`;

const TypingArea = styled.div`
  margin-bottom: 2rem;
  position: relative;
  text-align: left;
`;

const TextContainer = styled.div`
  position: relative;
  font-size: 1.2rem;
  line-height: 1.8;
  font-family: monospace;
`;

const BaseText = styled.div`
  white-space: pre-wrap;
  color: #666;
  position: relative;
  z-index: 1;
`;

const OverlayText = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  white-space: pre-wrap;
  z-index: 2;
`;

const Char = styled.span<{ status: 'correct' | 'incorrect' | 'waiting' }>`
  color: ${props => {
    switch (props.status) {
      case 'correct': return '#fff';
      case 'incorrect': return '#ff4444';
      case 'waiting': return 'transparent';
    }
  }};
`;

const ProgressBar = styled.div`
  width: 100%;
  height: 4px;
  background-color: #444;
  margin-bottom: 1rem;
  border-radius: 2px;
`;

const Progress = styled.div<{ width: number }>`
  width: ${props => props.width}%;
  height: 100%;
  background-color: ${props => props.width === 100 ? '#4CAF50' : '#666'};
  border-radius: 2px;
  transition: width 0.3s ease;
`;

const CompletionMessage = styled.div<{ show: boolean }>`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background-color: rgba(0, 0, 0, 0.9);
  padding: 2rem;
  border-radius: 8px;
  display: ${props => props.show ? 'block' : 'none'};
  z-index: 100;
  text-align: center;
`;

const HiddenInput = styled.input`
  position: absolute;
  opacity: 0;
  pointer-events: none;
`;

const poems = [
  {
    title: '대화',
    content: `그레이브스 의사시이 모든 것을 받아 적었다. 크리스티 의사가 말했다.
"걱이 난다라. 당신이 어떻게 도리가 걱이 난다는 뜻입니까?"
내가 말했다. "제가 아닙니다, 나리. 마님이 어떻게 될까 그러는 거죠. 마님이 자신을 해칠지도
모른다는 생각이 듭니다. 마님은 그 정도로 힘들어 하고 계십니다."
"알겠습니다" 크리스티 의사가 말했다. "당신은 마님을 좋아하시는군요. 그분에 대해 아주 좋은
말을 해주었지요. 자, 이제 이 질문에 답해 주십시오. 리버스 부인의 상태가 좋아지게 하려면 어떤
치료가 필요할 것 같다고 생각하십니까?"
내가 말했다. "제 생각에는......"
"생각에는?"
"가능하다면......"`,
  }
];

const App: React.FC = () => {
  const [userInput, setUserInput] = useState('');
  const [completed, setCompleted] = useState(false);
  const [composing, setComposing] = useState(false);
  const inputRef = useRef<HTMLInputElement>(null);
  const currentPoem = poems[0];

  const calculateProgress = () => {
    if (userInput.length === 0) return 0;
    const correctChars = userInput.split('').filter((char, i) => char === currentPoem.content[i]).length;
    return (correctChars / currentPoem.content.length) * 100;
  };

  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.focus();
    }
  }, []);

  const handleInput = (e: React.FormEvent<HTMLInputElement>) => {
    const newInput = e.currentTarget.value;
    setUserInput(newInput);

    if (newInput.length === currentPoem.content.length) {
      const isCorrect = newInput === currentPoem.content;
      if (isCorrect) {
        setCompleted(true);
      }
    }
  };

  const handleCompositionStart = () => {
    setComposing(true);
  };

  const handleCompositionEnd = () => {
    setComposing(false);
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Backspace' && !composing) {
      e.preventDefault();
      setUserInput(prev => prev.slice(0, -1));
    }
  };

  const renderOverlayText = () => {
    return currentPoem.content.split('').map((char, i) => {
      let status: 'correct' | 'incorrect' | 'waiting' = 'waiting';
      if (i < userInput.length) {
        status = userInput[i] === char ? 'correct' : 'incorrect';
      }
      return <Char key={i} status={status}>{char}</Char>;
    });
  };

  const progress = calculateProgress();

  return (
    <AppContainer onClick={() => inputRef.current?.focus()}>
      <Title>시 타이핑 연습</Title>
      <ProgressBar>
        <Progress width={progress} />
      </ProgressBar>
      <TypingArea>
        <TextContainer>
          <BaseText>{currentPoem.content}</BaseText>
          <OverlayText>{renderOverlayText()}</OverlayText>
        </TextContainer>
        <HiddenInput
          ref={inputRef}
          value={userInput}
          onChange={handleInput}
          onCompositionStart={handleCompositionStart}
          onCompositionEnd={handleCompositionEnd}
          onKeyDown={handleKeyDown}
          autoFocus
        />
      </TypingArea>
      <CompletionMessage show={completed}>
        <h2>🎉 축하합니다! 🎉</h2>
        <p>성공적으로 시를 완성하셨습니다!</p>
      </CompletionMessage>
    </AppContainer>
  );
};

export default App;
