import React, { useState, useEffect, useRef, createRef, RefObject } from 'react';
import styled from 'styled-components';

const AppContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem;
  text-align: center;
  background-color: rgb(255, 255, 255);
  min-height: 100vh;
  color: #000;
`;

const Title = styled.h1`
  color: black;
  margin-bottom: 2rem;
`;

const TypingArea = styled.div`
  margin-bottom: 2rem;
  position: relative;
  text-align: left;
`;

const TextContainer = styled.div`
  position: relative;
  font-size: 1.5rem;
  line-height: 1.8;
  font-family: monospace;
`;

const LineContainer = styled.div`
  position: relative;
  height: 1.8em;
  margin-bottom: 0.2em;
`;

const BaseLine = styled.div`
  white-space: pre;
  color: lightgray;
  position: absolute;
  top: 0;
  left: 0;
  font-family: monospace;
`;

const InputLine = styled.input`
  width: 100%;
  font-size: 1.5rem;
  line-height: 1.8;
  font-family: monospace;
  background: transparent;
  border: none;
  outline: none;
  color: transparent;
  caret-color: black;
  position: absolute;
  top: 0;
  left: 0;
  padding: 0;
`;

const OverlayLine = styled.div`
  white-space: pre;
  position: absolute;
  top: 0;
  left: 0;
  font-family: monospace;
  pointer-events: none;
`;

const Char = styled.span<{ status: 'correct' | 'incorrect' | 'waiting' | 'composing' }>`
  color: ${props => {
    switch (props.status) {
      case 'correct': return 'black';
      case 'incorrect': return '#ff4444';
      case 'waiting': return 'transparent';
      case 'composing': return '#aaffaa'; // Ï°∞Ìï© Ï§ëÏù∏ Í∏ÄÏûêÎäî Ïó∞Ìïú ÎÖπÏÉâ
    }
  }};
`;

const ProgressBar = styled.div`
  width: 100%;
  height: 4px;
  background-color: #444;
  margin-bottom: 1rem;
  border-radius: 2px;
`;

const Progress = styled.div<{ width: number }>`
  width: ${props => props.width}%;
  height: 100%;
  background-color: ${props => props.width === 100 ? '#4CAF50' : '#666'};
  border-radius: 2px;
  transition: width 0.3s ease;
`;

const CompletionMessage = styled.div<{ show: boolean }>`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background-color: rgba(0, 0, 0, 0.9);
  padding: 2rem;
  border-radius: 8px;
  display: ${props => props.show ? 'block' : 'none'};
  z-index: 100;
  text-align: center;
  color: white;
`;

const poems = [
  {
    title: 'ÎåÄÌôî',
    content: `ÏÇ¨ÎûëÏùÑ ÏûÉÍ≥† ÎÇòÎäî Ïì∞ÎÑ§
Ïûò ÏûàÍ±∞Îùº, ÏßßÏïòÎçò Î∞§Îì§ÏïÑ
Ï∞ΩÎ∞ñÏùÑ Îñ†ÎèåÎçò Í≤®Ïö∏ ÏïàÍ∞úÎì§ÏïÑ
ÏïÑÎ¨¥Í≤ÉÎèÑ Î™®Î•¥Îçò Ï¥õÎ∂àÎì§ÏïÑ, Ïûò ÏûàÍ±∞Îùº
Í≥µÌè¨Î•º Í∏∞Îã§Î¶¨Îçò Ìù∞ Ï¢ÖÏù¥Îì§ÏïÑ
ÎßêÏÑ§ÏûÑÏùÑ ÎåÄÏã†ÌïòÎçò ÎààÎ¨ºÎì§ÏïÑ
Ïûò ÏûàÍ±∞Îùº, Îçî Ïù¥ÏÉÅ ÎÇ¥ Í≤ÉÏù¥ ÏïÑÎãå Ïó¥ÎßùÎì§ÏïÑ
Ïû•ÎãòÏ≤òÎüº ÎÇò Ïù¥Ï†ú ÎçîÎì¨Í±∞Î¶¨Î©∞ Î¨∏ÏùÑ Ïû†Í∑∏ÎÑ§
Í∞ÄÏóæÏùÄ ÎÇ¥ ÏÇ¨Îûë ÎπàÏßëÏóê Í∞áÌòîÎÑ§`,
  }
];

// ÌïúÍ∏Ä Ïú†ÎãàÏΩîÎìú Î≤îÏúÑ Ï≤¥ÌÅ¨
const isKorean = (char: string) => {
  const code = char.charCodeAt(0);
  return code >= 0xAC00 && code <= 0xD7A3;
};

// Ï¥àÏÑ± Í≤ÄÏÇ¨
const isChoseong = (char: string) => {
  const code = char.charCodeAt(0);
  return code >= 0x3131 && code <= 0x314E;
};

// Ï§ëÏÑ± Í≤ÄÏÇ¨
const isJungseong = (char: string) => {
  const code = char.charCodeAt(0);
  return code >= 0x314F && code <= 0x3163;
};

const App: React.FC = () => {
  const currentPoem = poems[0];
  const poemLines = currentPoem.content.split('\n');

  // Í∞Å Ï§ÑÎ≥Ñ ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [lineInputs, setLineInputs] = useState<string[]>(Array(poemLines.length).fill(''));
  const [activeLineIndex, setActiveLineIndex] = useState(0);
  const [completed, setCompleted] = useState(false);
  const [composingLine, setComposingLine] = useState<number | null>(null);

  // Í∞Å Ï§ÑÏùò ÏûÖÎ†• ÌïÑÎìúÏóê ÎåÄÌïú ref ÏÉùÏÑ±
  const lineRefs = useRef<RefObject<HTMLInputElement>[]>(
    Array(poemLines.length).fill(null).map(() => createRef<HTMLInputElement>())
  );

  // ÏßÑÌñâ ÏÉÅÌô© Í≥ÑÏÇ∞
  const calculateProgress = () => {
    let correctChars = 0;
    let totalChars = 0;

    poemLines.forEach((line, index) => {
      const input = lineInputs[index] || '';
      for (let i = 0; i < Math.min(line.length, input.length); i++) {
        if (line[i] === input[i]) {
          correctChars++;
        }
      }
      totalChars += line.length;
    });

    return (correctChars / totalChars) * 100;
  };

  // Ï§Ñ ÏûÖÎ†• Ï≤òÎ¶¨
  const handleLineInput = (index: number, value: string) => {
    const newLineInputs = [...lineInputs];
    newLineInputs[index] = value;
    setLineInputs(newLineInputs);

    // ÌòÑÏû¨ Ï§ÑÏù¥ ÏôÑÏÑ±ÎêòÏóàÎäîÏßÄ ÌôïÏù∏
    const currentLine = poemLines[index];
    if (value === currentLine && index < poemLines.length - 1) {
      // Îã§Ïùå Ï§ÑÎ°ú ÏûêÎèô Ïù¥Îèô (ÏïΩÍ∞ÑÏùò ÏßÄÏó∞ Ï∂îÍ∞Ä)
      setTimeout(() => {
        setActiveLineIndex(index + 1);
        lineRefs.current[index + 1].current?.focus();
      }, 100);
    }

    // Ï†ÑÏ≤¥ ÏôÑÏÑ± Ïó¨Î∂Ä ÌôïÏù∏
    checkCompletion(newLineInputs);
  };

  // ÌÇ§ Ïù¥Î≤§Ìä∏ Ï≤òÎ¶¨
  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>, index: number) => {
    // ÏóîÌÑ∞ÌÇ§ Ï≤òÎ¶¨
    if (e.key === 'Enter') {
      e.preventDefault();

      // ÎßàÏßÄÎßâ Ï§ÑÏù¥ ÏïÑÎãàÎ©¥ Îã§Ïùå Ï§ÑÎ°ú Ïù¥Îèô
      if (index < poemLines.length - 1) {
        setActiveLineIndex(index + 1);
        lineRefs.current[index + 1].current?.focus();
      }
    }
    // ÏúÑ ÌôîÏÇ¥Ìëú Ï≤òÎ¶¨
    else if (e.key === 'ArrowUp' && index > 0) {
      e.preventDefault();
      setActiveLineIndex(index - 1);
      lineRefs.current[index - 1].current?.focus();
    }
    // ÏïÑÎûò ÌôîÏÇ¥Ìëú Ï≤òÎ¶¨
    else if (e.key === 'ArrowDown' && index < poemLines.length - 1) {
      e.preventDefault();
      setActiveLineIndex(index + 1);
      lineRefs.current[index + 1].current?.focus();
    }
  };

  // Ï°∞Ìï© Ïù¥Î≤§Ìä∏ Ï≤òÎ¶¨
  const handleCompositionStart = (index: number) => {
    setComposingLine(index);
  };

  const handleCompositionEnd = () => {
    setComposingLine(null);
  };

  // ÏôÑÏÑ± Ïó¨Î∂Ä ÌôïÏù∏
  const checkCompletion = (inputs: string[]) => {
    if (inputs.length !== poemLines.length) return false;

    const allComplete = poemLines.every((line, i) => line === inputs[i]);
    setCompleted(allComplete);
    return allComplete;
  };

  // Ï§Ñ Î†åÎçîÎßÅ
  const renderLine = (line: string, index: number) => {
    const input = lineInputs[index] || '';
    const isActive = index === activeLineIndex;
    const isComposing = index === composingLine;

    // Í∞Å Í∏ÄÏûê Î†åÎçîÎßÅ
    const chars = line.split('').map((char, i) => {
      // ÏûÖÎ†•Îêú Í∏ÄÏûêÍ∞Ä ÏûàÎäî Í≤ΩÏö∞
      if (i < input.length) {
        // Ï°∞Ìï© Ï§ëÏù¥Í≥† ÎßàÏßÄÎßâ Í∏ÄÏûêÏùº Í≤ΩÏö∞
        if (isComposing && i === input.length - 1) {
          return (
            <Char key={i} status="composing">
              {input[i]}
            </Char>
          );
        }

        // ÏùºÏπò Ïó¨Î∂ÄÏóê Îî∞Îùº Ïä§ÌÉÄÏùº Í≤∞Ï†ï
        const status = char === input[i] ? 'correct' : 'incorrect';
        return (
          <Char key={i} status={status}>
            {input[i]}
          </Char>
        );
      }

      // ÏûÖÎ†•ÎêòÏßÄ ÏïäÏùÄ Í∏ÄÏûêÎäî Í∏∞Îã§Î¶º ÏÉÅÌÉú
      return (
        <Char key={i} status="waiting">
          {char}
        </Char>
      );
    });

    return (
      <LineContainer key={index}>
        <BaseLine>{line}</BaseLine>
        <InputLine
          ref={lineRefs.current[index]}
          value={input}
          onChange={(e) => handleLineInput(index, e.target.value)}
          onKeyDown={(e) => handleKeyDown(e, index)}
          onCompositionStart={() => handleCompositionStart(index)}
          onCompositionEnd={handleCompositionEnd}
          disabled={!isActive}
          autoFocus={isActive && index === 0}
          spellCheck={false}
          autoComplete="off"
          data-line-index={index}
        />
        <OverlayLine>{chars}</OverlayLine>
      </LineContainer>
    );
  };

  // Ï¥àÍ∏∞ Ìè¨Ïª§Ïä§ ÏÑ§Ï†ï
  useEffect(() => {
    lineRefs.current[activeLineIndex].current?.focus();
  }, [activeLineIndex]);

  const progress = calculateProgress();

  return (
    <AppContainer>
      <Title>Ïãú ÌÉÄÏù¥Ìïë Ïó∞Ïäµ</Title>
      <ProgressBar>
        <Progress width={progress} />
      </ProgressBar>
      <TypingArea>
        <TextContainer>
          {poemLines.map((line, i) => renderLine(line, i))}
        </TextContainer>
      </TypingArea>
      <CompletionMessage show={completed}>
        <h2>üéâ Ï∂ïÌïòÌï©ÎãàÎã§! üéâ</h2>
        <p>ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏãúÎ•º ÏôÑÏÑ±ÌïòÏÖ®ÏäµÎãàÎã§!</p>
      </CompletionMessage>
    </AppContainer>
  );
};

export default App;
