import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';

const AppContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem;
  text-align: center;
  background-color: #2b2b2b;
  min-height: 100vh;
  color: #fff;
`;

const Title = styled.h1`
  color: #fff;
  margin-bottom: 2rem;
`;

const TypingArea = styled.div`
  margin-bottom: 2rem;
  position: relative;
  text-align: left;
`;

const TextContainer = styled.div`
  position: relative;
  font-size: 1.2rem;
  line-height: 1.8;
  font-family: monospace;
`;

const BaseText = styled.div`
  white-space: pre-wrap;
  color: #666;
  position: relative;
  z-index: 1;
`;

const OverlayText = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  white-space: pre-wrap;
  z-index: 2;
`;

const Char = styled.span<{ status: 'correct' | 'incorrect' | 'waiting' }>`
  color: ${props => {
    switch (props.status) {
      case 'correct': return '#fff';
      case 'incorrect': return '#ff4444';
      case 'waiting': return 'transparent';
    }
  }};
`;

const ProgressBar = styled.div`
  width: 100%;
  height: 4px;
  background-color: #444;
  margin-bottom: 1rem;
  border-radius: 2px;
`;

const Progress = styled.div<{ width: number }>`
  width: ${props => props.width}%;
  height: 100%;
  background-color: ${props => props.width === 100 ? '#4CAF50' : '#666'};
  border-radius: 2px;
  transition: width 0.3s ease;
`;

const CompletionMessage = styled.div<{ show: boolean }>`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background-color: rgba(0, 0, 0, 0.9);
  padding: 2rem;
  border-radius: 8px;
  display: ${props => props.show ? 'block' : 'none'};
  z-index: 100;
  text-align: center;
`;

const HiddenInput = styled.input`
  position: absolute;
  opacity: 0;
  pointer-events: none;
`;

const poems = [
  {
    title: 'ÎåÄÌôî',
    content: `Í∑∏Î†àÏù¥Î∏åÏä§ ÏùòÏÇ¨ÏãúÏù¥ Î™®Îì† Í≤ÉÏùÑ Î∞õÏïÑ Ï†ÅÏóàÎã§. ÌÅ¨Î¶¨Ïä§Ìã∞ ÏùòÏÇ¨Í∞Ä ÎßêÌñàÎã§.
"Í±±Ïù¥ ÎÇúÎã§Îùº. ÎãπÏã†Ïù¥ Ïñ¥ÎñªÍ≤å ÎèÑÎ¶¨Í∞Ä Í±±Ïù¥ ÎÇúÎã§Îäî ÎúªÏûÖÎãàÍπå?"
ÎÇ¥Í∞Ä ÎßêÌñàÎã§. "Ï†úÍ∞Ä ÏïÑÎãôÎãàÎã§, ÎÇòÎ¶¨. ÎßàÎãòÏù¥ Ïñ¥ÎñªÍ≤å Îê†Íπå Í∑∏Îü¨Îäî Í±∞Ï£†. ÎßàÎãòÏù¥ ÏûêÏã†ÏùÑ Ìï¥Ïπ†ÏßÄÎèÑ
Î™®Î•∏Îã§Îäî ÏÉùÍ∞ÅÏù¥ Îì≠ÎãàÎã§. ÎßàÎãòÏùÄ Í∑∏ Ï†ïÎèÑÎ°ú ÌûòÎì§Ïñ¥ ÌïòÍ≥† Í≥ÑÏã≠ÎãàÎã§."
"ÏïåÍ≤†ÏäµÎãàÎã§" ÌÅ¨Î¶¨Ïä§Ìã∞ ÏùòÏÇ¨Í∞Ä ÎßêÌñàÎã§. "ÎãπÏã†ÏùÄ ÎßàÎãòÏùÑ Ï¢ãÏïÑÌïòÏãúÎäîÍµ∞Ïöî. Í∑∏Î∂ÑÏóê ÎåÄÌï¥ ÏïÑÏ£º Ï¢ãÏùÄ
ÎßêÏùÑ Ìï¥Ï£ºÏóàÏßÄÏöî. Ïûê, Ïù¥Ï†ú Ïù¥ ÏßàÎ¨∏Ïóê ÎãµÌï¥ Ï£ºÏã≠ÏãúÏò§. Î¶¨Î≤ÑÏä§ Î∂ÄÏù∏Ïùò ÏÉÅÌÉúÍ∞Ä Ï¢ãÏïÑÏßÄÍ≤å ÌïòÎ†§Î©¥ Ïñ¥Îñ§
ÏπòÎ£åÍ∞Ä ÌïÑÏöîÌï† Í≤É Í∞ôÎã§Í≥† ÏÉùÍ∞ÅÌïòÏã≠ÎãàÍπå?"
ÎÇ¥Í∞Ä ÎßêÌñàÎã§. "Ï†ú ÏÉùÍ∞ÅÏóêÎäî......"
"ÏÉùÍ∞ÅÏóêÎäî?"
"Í∞ÄÎä•ÌïòÎã§Î©¥......"`,
  }
];

// ÌïúÍ∏Ä Ïú†ÎãàÏΩîÎìú Î≤îÏúÑ Ï≤¥ÌÅ¨
const isKorean = (char: string) => {
  const code = char.charCodeAt(0);
  return code >= 0xAC00 && code <= 0xD7A3;
};

// ÌïúÍ∏Ä ÏûêÎ™® Î∂ÑÎ¶¨
const decompose = (char: string) => {
  const code = char.charCodeAt(0) - 0xAC00;
  if (code < 0 || code > 11171) return null;

  const jong = code % 28;
  const jung = ((code - jong) / 28) % 21;
  const cho = ((code - jong) / 28 - jung) / 21;

  return {
    cho: Math.floor(cho),
    jung: Math.floor(jung),
    jong: Math.floor(jong)
  };
};

// Î∂ÄÎ∂Ñ ÏùºÏπò Í≤ÄÏÇ¨
const isPartialMatch = (target: string, input: string) => {
  if (!isKorean(target) || !isKorean(input)) {
    return target.startsWith(input);
  }

  const targetDecomp = decompose(target);
  const inputDecomp = decompose(input);

  if (!targetDecomp || !inputDecomp) return false;

  // Ï¥àÏÑ±Ïù¥ Îã§Î•¥Î©¥ Î¨¥Ï°∞Í±¥ Î∂àÏùºÏπò
  if (targetDecomp.cho !== inputDecomp.cho) return false;

  // Ï§ëÏÑ±Ïù¥ ÏûàÎäî Í≤ΩÏö∞ Ï§ëÏÑ±ÍπåÏßÄ ÎπÑÍµê
  if (inputDecomp.jung !== -1) {
    if (targetDecomp.jung !== inputDecomp.jung) return false;
  }

  // Ï¢ÖÏÑ±Ïù¥ ÏûàÎäî Í≤ΩÏö∞ Ï¢ÖÏÑ±ÍπåÏßÄ ÎπÑÍµê
  if (inputDecomp.jong !== -1) {
    if (targetDecomp.jong !== inputDecomp.jong) return false;
  }

  return true;
};

const App: React.FC = () => {
  const [userInput, setUserInput] = useState('');
  const [completed, setCompleted] = useState(false);
  const [composing, setComposing] = useState(false);
  const inputRef = useRef<HTMLInputElement>(null);
  const currentPoem = poems[0];

  const calculateProgress = () => {
    if (userInput.length === 0) return 0;
    const correctChars = userInput.split('').filter((char, i) => char === currentPoem.content[i]).length;
    return (correctChars / currentPoem.content.length) * 100;
  };

  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.focus();
    }
  }, []);

  const handleInput = (e: React.FormEvent<HTMLInputElement>) => {
    const newInput = e.currentTarget.value;
    setUserInput(newInput);

    if (newInput.length === currentPoem.content.length) {
      const isCorrect = newInput === currentPoem.content;
      if (isCorrect) {
        setCompleted(true);
      }
    }
  };

  const handleCompositionStart = () => {
    setComposing(true);
  };

  const handleCompositionEnd = () => {
    setComposing(false);
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Backspace' && !composing) {
      e.preventDefault();
      setUserInput(prev => prev.slice(0, -1));
    }
  };

  const renderOverlayText = () => {
    const userChars = userInput.split('');
    const targetChars = currentPoem.content.split('');

    return targetChars.map((char, i) => {
      let status: 'correct' | 'incorrect' | 'waiting' = 'waiting';

      if (i < userInput.length) {
        if (userChars[i] === char) {
          status = 'correct';
        } else if (composing && i === userInput.length - 1) {
          // ÌòÑÏû¨ Ï°∞Ìï© Ï§ëÏù∏ Í∏ÄÏûêÎäî Î∂ÄÎ∂Ñ ÏùºÏπò Í≤ÄÏÇ¨
          status = isPartialMatch(char, userChars[i]) ? 'correct' : 'incorrect';
        } else {
          status = 'incorrect';
        }
      }

      return <Char key={i} status={status}>{char}</Char>;
    });
  };

  const progress = calculateProgress();

  return (
    <AppContainer onClick={() => inputRef.current?.focus()}>
      <Title>Ïãú ÌÉÄÏù¥Ìïë Ïó∞Ïäµ</Title>
      <ProgressBar>
        <Progress width={progress} />
      </ProgressBar>
      <TypingArea>
        <TextContainer>
          <BaseText>{currentPoem.content}</BaseText>
          <OverlayText>{renderOverlayText()}</OverlayText>
        </TextContainer>
        <HiddenInput
          ref={inputRef}
          value={userInput}
          onChange={handleInput}
          onCompositionStart={handleCompositionStart}
          onCompositionEnd={handleCompositionEnd}
          onKeyDown={handleKeyDown}
          autoFocus
        />
      </TypingArea>
      <CompletionMessage show={completed}>
        <h2>üéâ Ï∂ïÌïòÌï©ÎãàÎã§! üéâ</h2>
        <p>ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏãúÎ•º ÏôÑÏÑ±ÌïòÏÖ®ÏäµÎãàÎã§!</p>
      </CompletionMessage>
    </AppContainer>
  );
};

export default App;
