const CHOSEONG = ['„Ñ±', '„Ñ≤', '„Ñ¥', '„Ñ∑', '„Ñ∏', '„Ñπ', '„ÖÅ', '„ÖÇ', '„ÖÉ', '„ÖÖ', '„ÖÜ', '„Öá', '„Öà', '„Öâ', '„Öä', '„Öã', '„Öå', '„Öç', '„Öé'];

// ÌïúÍ∏Ä ÏûêÎ™® Î∂ÑÎ¶¨
const decompose = (char: string) => {
  const code = char.charCodeAt(0) - 0xAC00;
  if (code < 0 || code > 11171) return null;

  const jong = code % 28;
  const jung = ((code - jong) / 28) % 21;
  const cho = ((code - jong) / 28 - jung) / 21;

  return {
    cho: Math.floor(cho),
    jung: Math.floor(jung),
    jong: Math.floor(jong)
  };
};

const App: React.FC = () => {
  // ÏÉÅÌÉú Í¥ÄÎ¶¨ Îã®ÏàúÌôî
  const [input, setInput] = useState('');
  const [composing, setComposing] = useState(false);
  const [cursorPosition, setCursorPosition] = useState(0);
  const [completed, setCompleted] = useState(false);

  // ÎîîÎ≤ÑÍ∑∏ Ï†ïÎ≥¥Î•º ÏúÑÌïú ÌÉÄÏûÖ Ï†ïÏùò
  interface DebugInfo {
    showDebug: boolean;
    event?: string;
    data?: string;
    currentInput?: string;
    cursorPosition?: number;
    isComposing?: boolean;
  }

  // ÎîîÎ≤ÑÍ∑∏ Ï†ïÎ≥¥
  const [debug, setDebug] = useState<DebugInfo>({ 
    showDebug: true 
  });

  const inputRef = useRef<HTMLInputElement>(null);
  const currentPoem = poems[0];

  // ÏãúÎ•º Ï§Ñ Îã®ÏúÑÎ°ú Î∂ÑÌï†
  const poemLines = currentPoem.content.split('\n');

  // ÏßÑÌñâ ÏÉÅÌô© Í≥ÑÏÇ∞
  const calculateProgress = () => {
    if (input.length === 0) return 0;
    const correctChars = input.split('').filter((char, i) => char === currentPoem.content[i]).length;
    return (correctChars / currentPoem.content.length) * 100;
  };

  // ÏûÖÎ†• Ï≤òÎ¶¨ Îã®ÏàúÌôî
  const handleInput = (e: React.ChangeEvent<HTMLInputElement>) => {
    const inputValue = e.target.value;
    setInput(inputValue);
    setCursorPosition(e.target.selectionStart || 0);

    // ÎîîÎ≤ÑÍ∑∏ Ï†ïÎ≥¥ ÏóÖÎç∞Ïù¥Ìä∏
    setDebug({
      showDebug: debug.showDebug,
      currentInput: inputValue,
      cursorPosition: e.target.selectionStart || 0,
      isComposing: composing
    });

    // ÏôÑÎ£å Ïó¨Î∂Ä ÌôïÏù∏
    const isComplete = checkLineCompletion(inputValue);
    if (isComplete) {
      setCompleted(true);
    }
  };

  // ÏôÑÎ£å Ïó¨Î∂Ä ÌôïÏù∏
  const checkLineCompletion = (value: string): boolean => {
    if (value.length > 0) {
      let allLinesMatch = true;
      const inputLines = value.split('\n');

      for (let i = 0; i < poemLines.length; i++) {
        if (i >= inputLines.length || inputLines[i] !== poemLines[i]) {
          allLinesMatch = false;
          break;
        }
      }

      return allLinesMatch && inputLines.length >= poemLines.length;
    }
    return false;
  };

  // Ï°∞Ìï© Ïù¥Î≤§Ìä∏ Ï≤òÎ¶¨ Îã®ÏàúÌôî
  const handleCompositionStart = () => {
    setComposing(true);
    setDebug({
      ...debug,
      event: 'composition start',
      isComposing: true
    });
  };

  const handleCompositionUpdate = (e: React.CompositionEvent<HTMLInputElement>) => {
    setDebug({
      ...debug,
      event: 'composition update',
      data: e.data || ''
    });
  };

  const handleCompositionEnd = () => {
    setComposing(false);
    setDebug({
      ...debug,
      event: 'composition end',
      isComposing: false
    });
  };

  // Ï§Ñ Îã®ÏúÑ Î†åÎçîÎßÅ Í∞ÑÏÜåÌôî
  const renderOverlayLine = (targetLine: string, lineIndex: number) => {
    // ÌòÑÏû¨ ÌÖçÏä§Ìä∏Î•º Ï§Ñ Îã®ÏúÑÎ°ú Î∂ÑÎ¶¨
    const textLines = input.split('\n');
    // ÌòÑÏû¨ Ï§ÑÏóê Ìï¥ÎãπÌïòÎäî ÌÖçÏä§Ìä∏
    const inputContent = lineIndex < textLines.length ? textLines[lineIndex] : '';

    // ÌòÑÏû¨ Ï§ÑÏùò ÏãúÏûë ÏúÑÏπò Í≥ÑÏÇ∞
    let lineStartPos = 0;
    for (let i = 0; i < lineIndex; i++) {
      lineStartPos += (i < textLines.length ? textLines[i].length : 0) + 1; // +1 for newline
    }

    // Ïª§ÏÑúÍ∞Ä ÌòÑÏû¨ Ï§ÑÏóê ÏûàÎäîÏßÄ ÌôïÏù∏
    const isCursorInThisLine = cursorPosition >= lineStartPos && 
      (lineIndex === textLines.length - 1 || cursorPosition < lineStartPos + inputContent.length + 1);

    // Ï§Ñ ÎÇ¥ Ïª§ÏÑú ÏúÑÏπò
    const cursorPositionInLine = isCursorInThisLine ? (cursorPosition - lineStartPos) : -1;

    // Í≤∞Í≥º Î†åÎçîÎßÅ
    const result: JSX.Element[] = [];
    let hasCursor = false;

    // Î™©Ìëú ÌÖçÏä§Ìä∏ Ï≤òÎ¶¨
    for (let i = 0; i < targetLine.length; i++) {
      const targetChar = targetLine[i];
      const isCursorHere = isCursorInThisLine && cursorPositionInLine === i && !hasCursor;

      if (i < inputContent.length) {
        // ÏûÖÎ†•Îêú Í∏ÄÏûê Ï≤òÎ¶¨
        const inputChar = inputContent[i];
        const status = targetChar === inputChar ? 'correct' : 'incorrect';

        result.push(
          <span key={i} style={{ position: 'relative' }}>
            <Char status={composing && i === inputContent.length - 1 ? 'composing' : status}>
              {inputChar}
            </Char>
            {isCursorHere && (hasCursor = true) && <Cursor />}
          </span>
        );
      } else {
        // ÏûÖÎ†•ÎêòÏßÄ ÏïäÏùÄ Í∏ÄÏûê Ï≤òÎ¶¨
        result.push(
          <span key={i} style={{ position: 'relative' }}>
            <Char status="waiting">{targetChar}</Char>
            {isCursorHere && (hasCursor = true) && <Cursor />}
          </span>
        );
      }
    }

    // Ï¥àÍ≥º ÏûÖÎ†• Ï≤òÎ¶¨
    if (inputContent.length > targetLine.length) {
      for (let i = targetLine.length; i < inputContent.length; i++) {
        const isCursorHere = isCursorInThisLine && cursorPositionInLine === i && !hasCursor;

        result.push(
          <span key={`excess-${i}`} style={{ position: 'relative' }}>
            <Char status={composing && i === inputContent.length - 1 ? 'composing' : 'incorrect'}>
              {inputContent[i]}
            </Char>
            {isCursorHere && (hasCursor = true) && <Cursor />}
          </span>
        );
      }
    }

    // ÎÅùÏóê Ïª§ÏÑú ÌëúÏãú
    if (isCursorInThisLine && cursorPositionInLine === inputContent.length && !hasCursor) {
      result.push(
        <span key="end-cursor" style={{ position: 'relative' }}>
          <Cursor />
        </span>
      );
    }

    return result;
  };

  const progress = calculateProgress();

  // Ï¥àÍ∏∞Ìôî Ïãú Ìè¨Ïª§Ïä§
  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.focus();
      setCursorPosition(0);
    }
  }, []);

  // Î†åÎçîÎßÅ
  return (
    <AppContainer onClick={() => inputRef.current?.focus()}>
      <Title>Ïãú ÌÉÄÏù¥Ìïë Ïó∞Ïäµ</Title>
      {/* ÎîîÎ≤ÑÍ∑∏ Ï†ïÎ≥¥ ÌëúÏãú (Í∞úÎ∞ú Î™®ÎìúÏóêÏÑúÎßå) */}
      {(process.env.NODE_ENV === 'development' || window.location.hostname === 'localhost') && (
        <div className="debug">
          <h3>ÎîîÎ≤ÑÍ∑∏ Ï†ïÎ≥¥:</h3>
          <pre>{JSON.stringify(debug, null, 2)}</pre>
        </div>
      )}
      <ProgressBar>
        <Progress width={progress} />
      </ProgressBar>
      <TypingArea>
        <TextContainer>
          {/* Ïãú ÎÇ¥Ïö© ÌëúÏãú */}
          <BaseText>{currentPoem.content}</BaseText>

          {/* ÏûÖÎ†• ÎÇ¥Ïö© Ïò§Î≤ÑÎ†àÏù¥ */}
          <OverlayText>
            {poemLines.map((line, i) => (
              <div key={i} style={{ whiteSpace: 'pre-wrap' }}>
                {renderOverlayLine(line, i)}
              </div>
            ))}
          </OverlayText>

          <HiddenInput
            ref={inputRef}
            value={input}
            onChange={handleInput}
            onCompositionStart={handleCompositionStart}
            onCompositionUpdate={handleCompositionUpdate}
            onCompositionEnd={handleCompositionEnd}
            autoFocus
            autoComplete="off"
            autoCorrect="off"
            spellCheck={false}
            aria-label="Ïãú ÏûÖÎ†• ÌïÑÎìú"
          />
        </TextContainer>
      </TypingArea>
      <CompletionMessage show={completed}>
        <h2>üéâ Ï∂ïÌïòÌï©ÎãàÎã§! üéâ</h2>
        <p>ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏãúÎ•º ÏôÑÏÑ±ÌïòÏÖ®ÏäµÎãàÎã§!</p>
      </CompletionMessage>
    </AppContainer>
  );
};

export default App;
