import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';

const AppContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem;
  text-align: center;
  background-color: #2b2b2b;
  min-height: 100vh;
  color: #fff;
`;

const Title = styled.h1`
  color: #fff;
  margin-bottom: 2rem;
`;

const TypingArea = styled.div`
  margin-bottom: 2rem;
  position: relative;
  text-align: left;
`;

const TextContainer = styled.div`
  position: relative;
  font-size: 1.2rem;
  line-height: 1.8;
  font-family: monospace;
`;

const BaseText = styled.div`
  white-space: pre-wrap;
  color: #666;
  position: relative;
  z-index: 1;
  min-height: 200px;
  font-family: monospace;
  line-height: 1.8em;
`;

const OverlayText = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  white-space: pre-wrap;
  z-index: 2;
  width: 100%;
  font-family: monospace;
  line-height: 1.8em;
`;

const Char = styled.span<{ status: 'correct' | 'incorrect' | 'waiting' | 'composing' }>`
  color: ${props => {
    switch (props.status) {
      case 'correct': return '#fff';
      case 'incorrect': return '#ff4444';
      case 'waiting': return '#666';
      case 'composing': return '#aaffaa'; // Ï°∞Ìï© Ï§ëÏù∏ Í∏ÄÏûêÎäî Ïó∞Ìïú ÎÖπÏÉâÏúºÎ°ú
    }
  }};
`;

const ProgressBar = styled.div`
  width: 100%;
  height: 4px;
  background-color: #444;
  margin-bottom: 1rem;
  border-radius: 2px;
`;

const Progress = styled.div<{ width: number }>`
  width: ${props => props.width}%;
  height: 100%;
  background-color: ${props => props.width === 100 ? '#4CAF50' : '#666'};
  border-radius: 2px;
  transition: width 0.3s ease;
`;

const CompletionMessage = styled.div<{ show: boolean }>`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background-color: rgba(0, 0, 0, 0.9);
  padding: 2rem;
  border-radius: 8px;
  display: ${props => props.show ? 'block' : 'none'};
  z-index: 100;
  text-align: center;
`;

const HiddenInput = styled.input`
  position: absolute;
  opacity: 0;
  pointer-events: auto;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  z-index: 3;
  cursor: text;
`;

const Cursor = styled.span`
  display: inline-block;
  width: 2px;
  height: 1.4em;
  background-color: #fff;
  animation: blink 1s step-end infinite;
  vertical-align: middle;
  position: absolute;
  top: 50%;
  transform: translateY(-50%);

  @keyframes blink {
    from, to {
      opacity: 1;
    }
    50% {
      opacity: 0;
    }
  }
`;

const poems = [
  {
    title: 'ÎåÄÌôî',
    content: `ÏÇ¨ÎûëÏùÑ ÏûÉÍ≥† ÎÇòÎäî Ïì∞ÎÑ§
Ïûò ÏûàÍ±∞Îùº, ÏßßÏïòÎçò Î∞§Îì§ÏïÑ
Ï∞ΩÎ∞ñÏùÑ Îñ†ÎèåÎçò Í≤®Ïö∏ ÏïàÍ∞úÎì§ÏïÑ
ÏïÑÎ¨¥Í≤ÉÎèÑ Î™®Î•¥Îçò Ï¥õÎ∂àÎì§ÏïÑ, Ïûò ÏûàÍ±∞Îùº
Í≥µÌè¨Î•º Í∏∞Îã§Î¶¨Îçò Ìù∞ Ï¢ÖÏù¥Îì§ÏïÑ
ÎßêÏÑ§ÏûÑÏùÑ ÎåÄÏã†ÌïòÎçò ÎààÎ¨ºÎì§ÏïÑ
Ïûò ÏûàÍ±∞Îùº, Îçî Ïù¥ÏÉÅ ÎÇ¥ Í≤ÉÏù¥ ÏïÑÎãå Ïó¥ÎßùÎì§ÏïÑ
Ïû•ÎãòÏ≤òÎüº ÎÇò Ïù¥Ï†ú ÎçîÎì¨Í±∞Î¶¨Î©∞ Î¨∏ÏùÑ Ïû†Í∑∏ÎÑ§
Í∞ÄÏóæÏùÄ ÎÇ¥ ÏÇ¨Îûë ÎπàÏßëÏóê Í∞áÌòîÎÑ§`,
  }
];

// ÌïúÍ∏Ä Ïú†ÎãàÏΩîÎìú Î≤îÏúÑ Ï≤¥ÌÅ¨
const isKorean = (char: string) => {
  const code = char.charCodeAt(0);
  return code >= 0xAC00 && code <= 0xD7A3;
};

// Ï¥àÏÑ± Í≤ÄÏÇ¨
const isChoseong = (char: string) => {
  const code = char.charCodeAt(0);
  return code >= 0x3131 && code <= 0x314E;
};

// Ï§ëÏÑ± Í≤ÄÏÇ¨
const isJungseong = (char: string) => {
  const code = char.charCodeAt(0);
  return code >= 0x314F && code <= 0x3163;
};

// Ï¥àÏÑ± ÌÖåÏù¥Î∏î
const CHOSEONG = ['„Ñ±', '„Ñ≤', '„Ñ¥', '„Ñ∑', '„Ñ∏', '„Ñπ', '„ÖÅ', '„ÖÇ', '„ÖÉ', '„ÖÖ', '„ÖÜ', '„Öá', '„Öà', '„Öâ', '„Öä', '„Öã', '„Öå', '„Öç', '„Öé'];

// ÌïúÍ∏Ä ÏûêÎ™® Î∂ÑÎ¶¨
const decompose = (char: string) => {
  const code = char.charCodeAt(0) - 0xAC00;
  if (code < 0 || code > 11171) return null;

  const jong = code % 28;
  const jung = ((code - jong) / 28) % 21;
  const cho = ((code - jong) / 28 - jung) / 21;

  return {
    cho: Math.floor(cho),
    jung: Math.floor(jung),
    jong: Math.floor(jong)
  };
};

const App: React.FC = () => {
  // ÌïµÏã¨ ÏÉÅÌÉú Î≥ÄÏàòÎì§
  const [userInput, setUserInput] = useState(''); // ÌôïÏ†ïÎêú ÏûÖÎ†• Î¨∏ÏûêÏó¥
  const [compositionText, setCompositionText] = useState(''); // Ï°∞Ìï© Ï§ëÏù∏ ÌÖçÏä§Ìä∏
  const [completed, setCompleted] = useState(false);
  const [composing, setComposing] = useState(false);

  // ÎîîÎ≤ÑÍ∑∏ Ï†ïÎ≥¥Î•º ÏúÑÌïú ÌÉÄÏûÖ Ï†ïÏùò
  interface DebugInfo {
    showDebug: boolean;
    event?: string;
    data?: string;
    currentInput?: string;
    composingText?: string;
    cursorPosition?: number;
    isComposing?: boolean;
  }

  // ÎîîÎ≤ÑÍ∑∏ Ï†ïÎ≥¥
  const [debug, setDebug] = useState<DebugInfo>({ 
    showDebug: true 
  });

  const [currentPosition, setCurrentPosition] = useState(0);
  const inputRef = useRef<HTMLInputElement>(null);
  const currentPoem = poems[0];

  // ÏãúÎ•º Ï§Ñ Îã®ÏúÑÎ°ú Î∂ÑÌï†
  const poemLines = currentPoem.content.split('\n');

  // ÏßÑÌñâ ÏÉÅÌô© Í≥ÑÏÇ∞
  const calculateProgress = () => {
    if (userInput.length === 0) return 0;
    const correctChars = userInput.split('').filter((char, i) => char === currentPoem.content[i]).length;
    return (correctChars / currentPoem.content.length) * 100;
  };

  // ÏûÖÎ†• Ïù¥Î≤§Ìä∏ Ìï∏Îì§Îü¨ - Ï£ºÎ°ú ÏòÅÎ¨∏/Ïà´Ïûê ÏûÖÎ†• Ïãú
  const handleInput = (e: React.ChangeEvent<HTMLInputElement>) => {
    const inputValue = e.target.value;

    // Ï°∞Ìï© Ï§ëÏù¥ ÏïÑÎãê ÎïåÎßå userInput ÏóÖÎç∞Ïù¥Ìä∏
    if (!composing) {
      setUserInput(inputValue);

      // Ïª§ÏÑú ÏúÑÏπò ÏÑ§Ï†ï
      const cursorPos = e.target.selectionStart || 0;
      setCurrentPosition(cursorPos);

      // ÏôÑÎ£å Ïó¨Î∂Ä ÌôïÏù∏
      const isComplete = checkLineCompletion(inputValue);
      if (isComplete) {
        setCompleted(true);
      }
    }

    // ÎîîÎ≤ÑÍ∑∏ Ï†ïÎ≥¥ ÏóÖÎç∞Ïù¥Ìä∏
    setDebug({
      showDebug: debug.showDebug,
      currentInput: userInput,
      composingText: compositionText,
      cursorPosition: e.target.selectionStart || 0,
      isComposing: composing
    });
  };

  // ÏôÑÎ£å Ïó¨Î∂Ä ÌôïÏù∏
  const checkLineCompletion = (value: string): boolean => {
    if (value.length > 0) {
      let allLinesMatch = true;
      const inputLines = value.split('\n');

      for (let i = 0; i < poemLines.length; i++) {
        if (i >= inputLines.length || inputLines[i] !== poemLines[i]) {
          allLinesMatch = false;
          break;
        }
      }

      return allLinesMatch && inputLines.length >= poemLines.length;
    }
    return false;
  };

  // IME Ï°∞Ìï©Ïù¥ ÏãúÏûëÎê† Îïå
  const handleCompositionStart = (e: React.CompositionEvent<HTMLInputElement>) => {
    setComposing(true);
    setCompositionText(''); // Ï°∞Ìï© Ï§ë ÌÖçÏä§Ìä∏ Ï¥àÍ∏∞Ìôî

    setDebug({ 
      showDebug: true,
      event: 'composition start', 
      data: '', 
      currentInput: userInput,
      composingText: '',
      isComposing: true
    });
  };

  // IME Ï°∞Ìï©Ïù¥ ÏßÑÌñâ Ï§ëÏùº Îïå
  const handleCompositionUpdate = (e: React.CompositionEvent<HTMLInputElement>) => {
    setCompositionText(e.data || ''); // Ï°∞Ìï© Ï§ë ÌÖçÏä§Ìä∏Îßå ÏóÖÎç∞Ïù¥Ìä∏

    setDebug({ 
      showDebug: true,
      event: 'composition update', 
      data: e.data || '', 
      currentInput: userInput,
      composingText: e.data || '',
      isComposing: true
    });
  };

  // IME Ï°∞Ìï©Ïù¥ ÏôÑÎ£åÎêêÏùÑ Îïå
  const handleCompositionEnd = (e: React.CompositionEvent<HTMLInputElement>) => {
    const finalText = e.data || ''; // ÏµúÏ¢ÖÏ†ÅÏúºÎ°ú Ï°∞Ìï©Îêú ÌÖçÏä§Ìä∏

    // Ï°∞Ìï© ÏôÑÎ£å ÏãúÏóêÎßå userInput ÏóÖÎç∞Ïù¥Ìä∏
    const newInput = userInput + finalText;
    setUserInput(newInput);
    setComposing(false);
    setCompositionText('');

    // Ïª§ÏÑú ÏúÑÏπò ÏóÖÎç∞Ïù¥Ìä∏
    const cursorPos = newInput.length;
    setCurrentPosition(cursorPos);

    // ÏôÑÎ£å Ïó¨Î∂Ä ÌôïÏù∏
    const isComplete = checkLineCompletion(newInput);
    if (isComplete) {
      setCompleted(true);
    }

    setDebug({ 
      showDebug: true,
      event: 'composition end', 
      data: finalText, 
      currentInput: newInput,
      composingText: '',
      isComposing: false,
      cursorPosition: cursorPos
    });
  };

  // ÌÇ§Î≥¥Îìú ÏûÖÎ†• Ï≤òÎ¶¨
  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    // ÌäπÏàò ÌÇ§ Ï≤òÎ¶¨Í∞Ä ÌïÑÏöîÌïòÎ©¥ Ïó¨Í∏∞ÏÑú Íµ¨ÌòÑ
  };

  // Ïã§Ï†ú ÌôîÎ©¥Ïóê ÌëúÏãúÌï† ÌÖçÏä§Ìä∏ = ÌôïÏ†ïÎêú userInput + Ï°∞Ìï© Ï§ëÏù∏ compositionText
  const displayText = composing ? userInput + compositionText : userInput;

  // Ï§Ñ Îã®ÏúÑ Î†åÎçîÎßÅ
  const renderOverlayLine = (targetLine: string, lineIndex: number) => {
    // ÌòÑÏû¨ ÌÖçÏä§Ìä∏Î•º Ï§Ñ Îã®ÏúÑÎ°ú Î∂ÑÎ¶¨
    const textLines = displayText.split('\n');
    // ÌòÑÏû¨ Ï§ÑÏóê Ìï¥ÎãπÌïòÎäî ÌÖçÏä§Ìä∏
    const inputContent = lineIndex < textLines.length ? textLines[lineIndex] : '';

    // ÌòÑÏû¨ Ï§ÑÏùò ÏãúÏûë ÏúÑÏπò Í≥ÑÏÇ∞ (Ï†ÑÏ≤¥ ÌÖçÏä§Ìä∏ÏóêÏÑúÏùò Ï†àÎåÄ ÏúÑÏπò)
    let lineStartPos = 0;
    for (let i = 0; i < lineIndex; i++) {
      lineStartPos += (i < textLines.length ? textLines[i].length : 0) + 1; // +1 for newline
    }

    // ÏûÖÎ†•Îêú Í∏ÄÏûê Ï§ë ÌôïÏ†ïÎêú Î∂ÄÎ∂ÑÏùò Í∏∏Ïù¥
    const confirmedLength = userInput.length - lineStartPos;
    const confirmedCharsInLine = Math.max(0, Math.min(confirmedLength, inputContent.length));

    // Ïª§ÏÑúÍ∞Ä ÌòÑÏû¨ Ï§ÑÏóê ÏûàÎäîÏßÄ ÌôïÏù∏
    const isCursorInThisLine = currentPosition >= lineStartPos && 
      (lineIndex === textLines.length - 1 || currentPosition < lineStartPos + inputContent.length + 1);

    // Ï§Ñ ÎÇ¥ Ïª§ÏÑú ÏúÑÏπò (ÏÉÅÎåÄÏ†Å ÏúÑÏπò)
    const cursorPositionInLine = isCursorInThisLine ? (currentPosition - lineStartPos) : -1;

    // Ï§Ñ ÎÇ¥ Î¨∏Ïûê Îã®ÏúÑ Ï≤òÎ¶¨
    const result: JSX.Element[] = [];
    let cursorRendered = false; // Ïª§ÏÑúÍ∞Ä Ïù¥ÎØ∏ Î†åÎçîÎßÅÎêòÏóàÎäîÏßÄ Ï∂îÏ†Å

    // Î™©Ìëú ÌÖçÏä§Ìä∏Ïùò Í∞Å Î¨∏ÏûêÏóê ÎåÄÌï¥ Ï≤òÎ¶¨
    for (let i = 0; i < targetLine.length; i++) {
      const targetChar = targetLine[i];

      // ÌòÑÏû¨ ÏúÑÏπòÏóê Ïª§ÏÑúÎ•º ÌëúÏãúÌï¥Ïïº ÌïòÎäîÏßÄ ÌåêÎã®
      const shouldShowCursor = isCursorInThisLine && cursorPositionInLine === i && !cursorRendered;

      if (i < inputContent.length) {
        // ÏûÖÎ†•Îêú Í∏ÄÏûê Ï≤òÎ¶¨
        const inputChar = inputContent[i];

        // Ïù¥ÎØ∏ ÌôïÏ†ïÎêú Í∏ÄÏûêÏù∏ÏßÄ ÌôïÏù∏ (Ï°∞Ìï© ÏôÑÎ£åÎêú Í∏ÄÏûê)
        const isConfirmed = i < confirmedCharsInLine;

        if (isConfirmed) {
          // Ïù¥ÎØ∏ ÌôïÏ†ïÎêú Í∏ÄÏûêÎäî Í≤∞Í≥ºÏóê Îî∞Îùº ÏÉâÏÉÅ Í≤∞Ï†ï
          const status = targetChar === inputChar ? 'correct' : 'incorrect';
          result.push(
            <span key={i} style={{ position: 'relative' }}>
              <Char status={status}>{inputChar}</Char>
              {shouldShowCursor && (cursorRendered = true) && <Cursor />}
            </span>
          );
        } else {
          // Ï°∞Ìï© Ï§ëÏù∏ Í∏ÄÏûêÎäî Î≥ÑÎèÑ Ïä§ÌÉÄÏùºÎ°ú ÌëúÏãú
          result.push(
            <span key={i} style={{ position: 'relative' }}>
              <Char status="composing">{inputChar}</Char>
              {shouldShowCursor && (cursorRendered = true) && <Cursor />}
            </span>
          );
        }
      } else {
        // ÏïÑÏßÅ ÏûÖÎ†•ÎêòÏßÄ ÏïäÏùÄ Í∏ÄÏûêÎäî ÎåÄÍ∏∞ ÏÉÅÌÉúÎ°ú ÌëúÏãú
        result.push(
          <span key={i} style={{ position: 'relative' }}>
            <Char status="waiting">{targetChar}</Char>
            {shouldShowCursor && (cursorRendered = true) && <Cursor />}
          </span>
        );
      }
    }

    // Ï¥àÍ≥º ÏûÖÎ†•Îêú Í∏ÄÏûê Ï≤òÎ¶¨
    if (inputContent.length > targetLine.length) {
      for (let i = targetLine.length; i < inputContent.length; i++) {
        const shouldShowCursor = isCursorInThisLine && cursorPositionInLine === i && !cursorRendered;
        const isConfirmed = i < confirmedCharsInLine;

        result.push(
          <span key={`excess-${i}`} style={{ position: 'relative' }}>
            <Char status={isConfirmed ? 'incorrect' : 'composing'}>{inputContent[i]}</Char>
            {shouldShowCursor && (cursorRendered = true) && <Cursor />}
          </span>
        );
      }
    }

    // ÌòÑÏû¨ Ï§ÑÏùò ÎÅùÏóê Ïª§ÏÑúÍ∞Ä ÏûàÎäî Í≤ΩÏö∞ (ÏïÑÏßÅ Ïª§ÏÑúÍ∞Ä ÌëúÏãúÎêòÏßÄ ÏïäÏïòÏùÑ ÎïåÎßå)
    const isCursorAtEndOfLine = isCursorInThisLine && cursorPositionInLine === inputContent.length && !cursorRendered;
    if (isCursorAtEndOfLine) {
      result.push(
        <span key="end-cursor" style={{ position: 'relative' }}>
          <Cursor />
        </span>
      );
      cursorRendered = true;
    }

    return result;
  };

  const progress = calculateProgress();

  // Ï¥àÍ∏∞Ìôî Ïãú Ïª§ÏÑú ÏúÑÏπòÎ•º 0ÏúºÎ°ú ÏÑ§Ï†ï
  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.focus();
      // Ïª§ÏÑú ÏúÑÏπòÎ•º Ï†ïÌôïÌûà 0ÏúºÎ°ú ÏÑ§Ï†ï
      setCurrentPosition(0);
      inputRef.current.setSelectionRange(0, 0);
    }
  }, []); // ÏùòÏ°¥ÏÑ± Î∞∞Ïó¥ ÎπÑÏõÄ

  // ÏÉÅÌÉúÍ∞Ä Î≥ÄÍ≤ΩÎê† ÎïåÎßàÎã§ ÏôÑÎ£å Ïó¨Î∂Ä Ï≤¥ÌÅ¨
  useEffect(() => {
    const displayTextToCheck = composing ? userInput : displayText;
    const isComplete = checkLineCompletion(displayTextToCheck);
    if (isComplete) {
      setCompleted(true);
    }
  }, [userInput, compositionText, composing, displayText]);

  return (
    <AppContainer onClick={() => inputRef.current?.focus()}>
      <Title>Ïãú ÌÉÄÏù¥Ìïë Ïó∞Ïäµ</Title>
      {/* ÎîîÎ≤ÑÍ∑∏ Ï†ïÎ≥¥ ÌëúÏãú (Í∞úÎ∞ú Î™®ÎìúÏóêÏÑúÎßå) */}
      {(process.env.NODE_ENV === 'development' || window.location.hostname === 'localhost') && (
        <div className="debug">
          <h3>ÎîîÎ≤ÑÍ∑∏ Ï†ïÎ≥¥:</h3>
          <pre>{JSON.stringify(debug, null, 2)}</pre>
        </div>
      )}
      <ProgressBar>
        <Progress width={progress} />
      </ProgressBar>
      <TypingArea>
        <TextContainer>
          {/* Ïãú ÎÇ¥Ïö© ÌëúÏãú */}
          <BaseText>{currentPoem.content}</BaseText>

          {/* ÏûÖÎ†• ÎÇ¥Ïö© Ïò§Î≤ÑÎ†àÏù¥ */}
          <OverlayText>
            {poemLines.map((line, i) => (
              <div key={i} style={{ whiteSpace: 'pre-wrap' }}>
                {renderOverlayLine(line, i)}
              </div>
            ))}
          </OverlayText>

          <HiddenInput
            ref={inputRef}
            value={composing ? userInput + compositionText : userInput}
            onChange={handleInput}
            onCompositionStart={handleCompositionStart}
            onCompositionUpdate={handleCompositionUpdate}
            onCompositionEnd={handleCompositionEnd}
            onKeyDown={handleKeyDown}
            autoFocus
            autoComplete="off"
            autoCorrect="off"
            spellCheck={false}
            aria-label="Ïãú ÏûÖÎ†• ÌïÑÎìú"
          />
        </TextContainer>
      </TypingArea>
      <CompletionMessage show={completed}>
        <h2>üéâ Ï∂ïÌïòÌï©ÎãàÎã§! üéâ</h2>
        <p>ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏãúÎ•º ÏôÑÏÑ±ÌïòÏÖ®ÏäµÎãàÎã§!</p>
      </CompletionMessage>
    </AppContainer>
  );
};

export default App;
