import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';

const AppContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem;
  text-align: center;
  background-color: #2b2b2b;
  min-height: 100vh;
  color: #fff;
`;

const Title = styled.h1`
  color: #fff;
  margin-bottom: 2rem;
`;

const TypingArea = styled.div`
  margin-bottom: 2rem;
  position: relative;
  text-align: left;
`;

const TextContainer = styled.div`
  position: relative;
  font-size: 1.2rem;
  line-height: 1.8;
  font-family: monospace;
`;

const BaseText = styled.div`
  white-space: pre-wrap;
  color: #666;
  position: relative;
  z-index: 1;
  min-height: 200px;
  font-family: monospace;
  line-height: 1.8em;
`;

const OverlayText = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  white-space: pre-wrap;
  z-index: 2;
  width: 100%;
  font-family: monospace;
  line-height: 1.8em;
`;

const Char = styled.span<{ status: 'correct' | 'incorrect' | 'waiting' }>`
  color: ${props => {
    switch (props.status) {
      case 'correct': return '#fff';
      case 'incorrect': return '#ff4444';
      case 'waiting': return '#666';
    }
  }};
`;

const ProgressBar = styled.div`
  width: 100%;
  height: 4px;
  background-color: #444;
  margin-bottom: 1rem;
  border-radius: 2px;
`;

const Progress = styled.div<{ width: number }>`
  width: ${props => props.width}%;
  height: 100%;
  background-color: ${props => props.width === 100 ? '#4CAF50' : '#666'};
  border-radius: 2px;
  transition: width 0.3s ease;
`;

const CompletionMessage = styled.div<{ show: boolean }>`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background-color: rgba(0, 0, 0, 0.9);
  padding: 2rem;
  border-radius: 8px;
  display: ${props => props.show ? 'block' : 'none'};
  z-index: 100;
  text-align: center;
`;

const HiddenInput = styled.input`
  position: absolute;
  opacity: 0;
  pointer-events: auto;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  z-index: 3;
  cursor: text;
`;

const Cursor = styled.span`
  display: inline-block;
  width: 2px;
  height: 1.4em;
  background-color: #fff;
  animation: blink 1s step-end infinite;
  vertical-align: middle;
  position: absolute;
  top: 50%;
  transform: translateY(-50%);

  @keyframes blink {
    from, to {
      opacity: 1;
    }
    50% {
      opacity: 0;
    }
  }
`;

const poems = [
  {
    title: 'ÎåÄÌôî',
    content: `ÏÇ¨ÎûëÏùÑ ÏûÉÍ≥† ÎÇòÎäî Ïì∞ÎÑ§
Ïûò ÏûàÍ±∞Îùº, ÏßßÏïòÎçò Î∞§Îì§ÏïÑ
Ï∞ΩÎ∞ñÏùÑ Îñ†ÎèåÎçò Í≤®Ïö∏ ÏïàÍ∞úÎì§ÏïÑ
ÏïÑÎ¨¥Í≤ÉÎèÑ Î™®Î•¥Îçò Ï¥õÎ∂àÎì§ÏïÑ, Ïûò ÏûàÍ±∞Îùº
Í≥µÌè¨Î•º Í∏∞Îã§Î¶¨Îçò Ìù∞ Ï¢ÖÏù¥Îì§ÏïÑ
ÎßêÏÑ§ÏûÑÏùÑ ÎåÄÏã†ÌïòÎçò ÎààÎ¨ºÎì§ÏïÑ
Ïûò ÏûàÍ±∞Îùº, Îçî Ïù¥ÏÉÅ ÎÇ¥ Í≤ÉÏù¥ ÏïÑÎãå Ïó¥ÎßùÎì§ÏïÑ
Ïû•ÎãòÏ≤òÎüº ÎÇò Ïù¥Ï†ú ÎçîÎì¨Í±∞Î¶¨Î©∞ Î¨∏ÏùÑ Ïû†Í∑∏ÎÑ§
Í∞ÄÏóæÏùÄ ÎÇ¥ ÏÇ¨Îûë ÎπàÏßëÏóê Í∞áÌòîÎÑ§`,
  }
];

// ÌïúÍ∏Ä Ïú†ÎãàÏΩîÎìú Î≤îÏúÑ Ï≤¥ÌÅ¨
const isKorean = (char: string) => {
  const code = char.charCodeAt(0);
  return code >= 0xAC00 && code <= 0xD7A3;
};

// Ï¥àÏÑ± Í≤ÄÏÇ¨
const isChoseong = (char: string) => {
  const code = char.charCodeAt(0);
  return code >= 0x3131 && code <= 0x314E;
};

// Ï¥àÏÑ± ÌÖåÏù¥Î∏î
const CHOSEONG = ['„Ñ±', '„Ñ≤', '„Ñ¥', '„Ñ∑', '„Ñ∏', '„Ñπ', '„ÖÅ', '„ÖÇ', '„ÖÉ', '„ÖÖ', '„ÖÜ', '„Öá', '„Öà', '„Öâ', '„Öä', '„Öã', '„Öå', '„Öç', '„Öé'];

// ÌïúÍ∏Ä ÏûêÎ™® Î∂ÑÎ¶¨
const decompose = (char: string) => {
  const code = char.charCodeAt(0) - 0xAC00;
  if (code < 0 || code > 11171) return null;

  const jong = code % 28;
  const jung = ((code - jong) / 28) % 21;
  const cho = ((code - jong) / 28 - jung) / 21;

  return {
    cho: Math.floor(cho),
    jung: Math.floor(jung),
    jong: Math.floor(jong)
  };
};

// ÌòÑÏû¨ ÏûÖÎ†•Ïù¥ Î™©ÌëúÏùò ÏùºÎ∂ÄÏù∏ÏßÄ ÌôïÏù∏ (Î°úÏßÅ Í∞úÏÑ†)
const isPartialMatch = (target: string, input: string) => {
  // ÌïúÍ∏ÄÏù¥ ÏïÑÎãàÎ©¥ Ï†ïÌôïÌûà ÏùºÏπòÌï¥Ïïº Ìï®
  if (!isKorean(target)) return target === input;

  // ÏôÑÏ†ÑÌûà ÏùºÏπòÌïòÎäî Í≤ΩÏö∞
  if (target === input) return true;

  // Ï¥àÏÑ±Îßå ÏûÖÎ†•Îêú Í≤ΩÏö∞ (Ïòà: Î™©ÌëúÍ∞Ä 'ÏÇ¨', ÏûÖÎ†•Ïù¥ '„ÖÖ')
  if (isChoseong(input)) {
    const targetDecomp = decompose(target);
    if (targetDecomp) {
      return CHOSEONG[targetDecomp.cho] === input;
    }
  }

  // Ï§ëÏÑ±Îßå ÏûÖÎ†•Îêú Í≤ΩÏö∞ (Ï§ëÏÑ± Í≤ÄÏÇ¨ Ìï®Ïàò ÏßÅÏ†ë ÏÇ¨Ïö©)
  const isJungseong = (char: string) => {
    const code = char.charCodeAt(0);
    return code >= 0x314F && code <= 0x3163;
  };

  if (isJungseong(input)) {
    return false; // Ï§ëÏÑ±Îßå ÏûàÎäî Í≤ΩÏö∞Îäî ÏùºÏπòÌïòÏßÄ ÏïäÏùå
  }

  // Ï¢ÖÏÑ±ÍπåÏßÄ ÏûÖÎ†•Îêú Í≤ΩÏö∞ (Ïòà: Î™©ÌëúÍ∞Ä 'Îùº', ÏûÖÎ†•Ïù¥ 'ÎûÑ')
  if (input.length === 1 && isKorean(input)) {
    const inputDecomp = decompose(input);
    const targetDecomp = decompose(target);

    if (inputDecomp && targetDecomp) {
      // Ï¥àÏÑ±Í≥º Ï§ëÏÑ±Ïù¥ ÏùºÏπòÌïòÎ©¥ Î∂ÄÎ∂Ñ ÏùºÏπòÎ°ú Í∞ÑÏ£º
      return inputDecomp.cho === targetDecomp.cho && 
             inputDecomp.jung === targetDecomp.jung;
    }
  }

  return false;
};

const App: React.FC = () => {
  const [userInput, setUserInput] = useState('');
  const [completed, setCompleted] = useState(false);
  const [composing, setComposing] = useState(false);
  const [currentText, setCurrentText] = useState('');
  const [currentComposition, setCurrentComposition] = useState('');

  // ÎîîÎ≤ÑÍ∑∏ Ï†ïÎ≥¥Î•º ÏúÑÌïú ÌÉÄÏûÖ Ï†ïÏùò
  interface DebugInfo {
    showDebug: boolean;
    event?: string;
    data?: string;
    currentText?: string;
    cursorPosition?: number;
    isComposing?: boolean;
  }

  // ÎîîÎ≤ÑÍ∑∏ Ï†ïÎ≥¥
  const [debug, setDebug] = useState<DebugInfo>({ 
    showDebug: true 
  });

  const [currentPosition, setCurrentPosition] = useState(0);
  const [currentLine, setCurrentLine] = useState(0);
  const inputRef = useRef<HTMLInputElement>(null);
  const currentPoem = poems[0];

  // ÏãúÎ•º Ï§Ñ Îã®ÏúÑÎ°ú Î∂ÑÌï†
  const poemLines = currentPoem.content.split('\n');

  const calculateProgress = () => {
    if (userInput.length === 0) return 0;
    const correctChars = userInput.split('').filter((char, i) => char === currentPoem.content[i]).length;
    return (correctChars / currentPoem.content.length) * 100;
  };

  // ÌòÑÏû¨ Ï§ÑÏù¥ ÏôÑÏÑ±ÎêòÏóàÎäîÏßÄ ÌôïÏù∏ÌïòÎäî Ìï®Ïàò (ÏûêÎèô Ï§ÑÎ∞îÍøà ÎπÑÌôúÏÑ±Ìôî)
  const checkLineCompletion = () => {
    // ÏûêÎèô Ï§ÑÎ∞îÍøà ÎπÑÌôúÏÑ±Ìôî
    return;
  };

  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.focus();
    }
  }, []);

  // useEffect ÏùòÏ°¥ÏÑ± Î∞∞Ïó¥ ÏàòÏ†ï
  useEffect(() => {
    // Ï°∞Ìï© Ï§ëÏùº ÎïåÎäî Ï≤¥ÌÅ¨ÌïòÏßÄ ÏïäÏùå
    if (!composing) {
      checkLineCompletion();
    }
  }, [userInput, composing, currentLine, poemLines]); // checkLineCompletion Ï†úÍ±∞

  // ÏûÖÎ†• Ï≤òÎ¶¨ Ìï®Ïàò (ÌòÑÏû¨ Ï§Ñ ÏóÖÎç∞Ïù¥Ìä∏ Ìè¨Ìï®)
  const handleInput = (e: React.ChangeEvent<HTMLInputElement>) => {
    const inputValue = e.target.value;
    const cursorPos = e.target.selectionStart || 0;

    setCurrentText(inputValue);
    setCurrentPosition(cursorPos);

    // ÎîîÎ≤ÑÍ∑∏ Ï†ïÎ≥¥ ÏóÖÎç∞Ïù¥Ìä∏
    setDebug({
      ...debug,
      inputValue,
      cursorPos,
      currentPosition: cursorPos,
    });

    // ÏôÑÎ£å ÌôïÏù∏
    checkCompletion(inputValue);
  };

  // IME Ï°∞Ìï©Ïù¥ ÏãúÏûëÎê† Îïå Ìò∏Ï∂úÎê©ÎãàÎã§
  const handleCompositionStart = (e: React.CompositionEvent<HTMLInputElement>) => {
    setComposing(true);
    setDebug({ 
      showDebug: true,
      event: 'composition start', 
      data: e.data || '' + ' / value: ' + e.currentTarget.value 
    });
  };

  // IME Ï°∞Ìï©Ïù¥ ÏßÑÌñâ Ï§ëÏùº Îïå Ìò∏Ï∂úÎê©ÎãàÎã§
  const handleCompositionUpdate = (e: React.CompositionEvent<HTMLInputElement>) => {
    const value = e.currentTarget.value;
    setCurrentText(value);
    setDebug({ 
      showDebug: true,
      event: 'composition update', 
      data: e.data || '' + ' / value: ' + value
    });
  };

  // IME Ï°∞Ìï©Ïù¥ ÏôÑÎ£åÎê† Îïå Ìò∏Ï∂úÎê©ÎãàÎã§
  const handleCompositionEnd = (e: React.CompositionEvent<HTMLInputElement>) => {
    const finalValue = e.currentTarget.value;
    setComposing(false);
    setCurrentText(finalValue);
    setUserInput(finalValue);

    // Ïª§ÏÑú ÏúÑÏπò ÏÑ§Ï†ï - ÏÑ†ÌÉù ÏúÑÏπò ÏÇ¨Ïö©
    const cursorPos = e.currentTarget.selectionStart || finalValue.length;
    setCurrentPosition(cursorPos);

    setDebug({ 
      showDebug: true,
      event: 'composition end', 
      data: e.data || '' + ' / value: ' + finalValue + ' / cursor: ' + cursorPos
    });
  };

  // ÌÇ§Î≥¥Îìú ÏûÖÎ†• Ï≤òÎ¶¨ Ìï®Ïàò Îã®ÏàúÌôî - Ïù¥Î≤§Ìä∏Ïùò Í∏∞Î≥∏ ÎèôÏûë Ïú†ÏßÄ
  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    // ÏïÑÎ¨¥ ÏûëÏóÖÎèÑ ÌïòÏßÄ ÏïäÏùå - Î∏åÎùºÏö∞Ï†Ä Í∏∞Î≥∏ ÎèôÏûë Ïú†ÏßÄ
    // Í∏∞Î≥∏Ï†ÅÏúºÎ°ú Î∞±Ïä§ÌéòÏù¥Ïä§ÏôÄ ÏóîÌÑ∞ ÌÇ§Îäî <input> ÏöîÏÜåÏóêÏÑú ÏòàÏÉÅÎåÄÎ°ú ÏûëÎèôÌï®
  };

  // Ï§ÑÎ∞îÍøà Ï≤òÎ¶¨Î•º ÏúÑÌïú Ìï®Ïàò ÏàòÏ†ï
  const renderOverlayLine = (targetLine: string, lineIndex: number) => {
    // ÌòÑÏû¨ ÌÖçÏä§Ìä∏Î•º Ï§Ñ Îã®ÏúÑÎ°ú Î∂ÑÎ¶¨
    const textLines = currentText.split('\n');
    // ÌòÑÏû¨ Ï§ÑÏóê Ìï¥ÎãπÌïòÎäî ÌÖçÏä§Ìä∏ (Ìï¥Îãπ Ï§Ñ Ïù∏Îç±Ïä§Í∞Ä ÏóÜÏúºÎ©¥ Îπà Î¨∏ÏûêÏó¥)
    const inputContent = lineIndex < textLines.length ? textLines[lineIndex] : '';

    // ÌòÑÏû¨ Ï§ÑÏùò ÏãúÏûë ÏúÑÏπò Í≥ÑÏÇ∞ (Ï†ÑÏ≤¥ ÌÖçÏä§Ìä∏ÏóêÏÑúÏùò Ï†àÎåÄ ÏúÑÏπò)
    let lineStartPos = 0;
    for (let i = 0; i < lineIndex; i++) {
      lineStartPos += poemLines[i].length + 1; // +1 for newline
    }

    // Ï§Ñ ÎÇ¥ Î¨∏Ïûê Îã®ÏúÑ Ï≤òÎ¶¨
    const result: JSX.Element[] = [];
    for (let i = 0; i < targetLine.length; i++) {
      const targetChar = targetLine[i];
      const inputChar = i < inputContent.length ? inputContent[i] : '';
      const globalCharPos = lineStartPos + i; // Ï†ÑÏ≤¥ ÌÖçÏä§Ìä∏ÏóêÏÑúÏùò ÏúÑÏπò
      const isCursorHere = globalCharPos === currentPosition;

      if (i < inputContent.length) {
        // ÏûÖÎ†•Îêú Í∏ÄÏûê Ï≤òÎ¶¨
        const status = targetChar === inputChar ? 'correct' : 'incorrect';
        result.push(
          <span key={i} style={{ position: 'relative' }}>
            <Char status={status}>{inputChar}</Char>
            {isCursorHere && <Cursor />}
          </span>
        );
      } else {
        // ÏûÖÎ†•ÎêòÏßÄ ÏïäÏùÄ Í∏ÄÏûêÎäî Í∏∞Îã§Î¶º ÏÉÅÌÉúÎ°ú ÌëúÏãú
        result.push(
          <span key={i} style={{ position: 'relative' }}>
            <Char status="waiting">{targetChar}</Char>
            {isCursorHere && <Cursor />}
          </span>
        );
      }
    }

    // Ï¥àÍ≥º ÏûÖÎ†•Îêú Í∏ÄÏûêÎì§ Ï≤òÎ¶¨ (Ï†ïÌôïÌûà Í∑∏ Ï§ÑÏóê ÎåÄÌï¥ÏÑúÎßå)
    if (inputContent.length > targetLine.length) {
      for (let i = targetLine.length; i < inputContent.length; i++) {
        const globalCharPos = lineStartPos + i;
        const isCursorHere = globalCharPos === currentPosition;
        result.push(
          <span key={`excess-${i}`} style={{ position: 'relative' }}>
            <Char status="incorrect">{inputContent[i]}</Char>
            {isCursorHere && <Cursor />}
          </span>
        );
      }
    }

    // ÌòÑÏû¨ Ï§ÑÏùò ÎÅùÏóê Ïª§ÏÑúÍ∞Ä ÏûàÎäî Í≤ΩÏö∞
    const isEndOfLine = currentPosition === lineStartPos + inputContent.length;
    if (isEndOfLine) {
      result.push(
        <span key="end-cursor" style={{ position: 'relative' }}>
          <Cursor />
        </span>
      );
    }

    return result;
  };

  const progress = calculateProgress();

  // Ï¥àÍ∏∞Ìôî Ïãú Ïª§ÏÑú ÏúÑÏπòÎ•º 0ÏúºÎ°ú ÏÑ§Ï†ï
  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.focus();
      setCurrentPosition(0);
    }
  }, []); // ÏùòÏ°¥ÏÑ± Î∞∞Ïó¥ ÎπÑÏõåÎë†

  useEffect(() => {
    checkLineCompletion();
  }, [currentLine, currentText, checkLineCompletion]);

  // Î†åÎçîÎßÅ Î∂ÄÎ∂Ñ ÏàòÏ†ï - Ïª§ÏÑú ÌïòÎÇòÎßå ÌëúÏãúÌïòÎèÑÎ°ù Î≥ÄÍ≤Ω
  return (
    <AppContainer onClick={() => inputRef.current?.focus()}>
      <Title>Ïãú ÌÉÄÏù¥Ìïë Ïó∞Ïäµ</Title>
      {/* ÎîîÎ≤ÑÍ∑∏ Ï†ïÎ≥¥ ÌëúÏãú (Í∞úÎ∞ú Î™®ÎìúÏóêÏÑúÎßå) */}
      {(process.env.NODE_ENV === 'development' || window.location.hostname === 'localhost') && (
        <div className="debug">
          <h3>ÎîîÎ≤ÑÍ∑∏ Ï†ïÎ≥¥:</h3>
          <pre>{JSON.stringify(debug, null, 2)}</pre>
        </div>
      )}
      <ProgressBar>
        <Progress width={progress} />
      </ProgressBar>
      <TypingArea>
        <TextContainer>
          {/* Ïãú ÎÇ¥Ïö© ÌëúÏãú */}
          <BaseText>{currentPoem.content}</BaseText>

          {/* ÏûÖÎ†• ÎÇ¥Ïö© Ïò§Î≤ÑÎ†àÏù¥ */}
          <OverlayText>
            {poemLines.map((line, i) => (
              <div key={i} style={{ whiteSpace: 'pre-wrap' }}>
                {renderOverlayLine(line, i)}
              </div>
            ))}
          </OverlayText>

          <HiddenInput
            ref={inputRef}
            value={currentText}
            onChange={handleInput}
            onCompositionStart={handleCompositionStart}
            onCompositionUpdate={handleCompositionUpdate}
            onCompositionEnd={handleCompositionEnd}
            onKeyDown={handleKeyDown}
            autoFocus
            autoComplete="off"
            autoCorrect="off"
            spellCheck={false}
            aria-label="Ïãú ÏûÖÎ†• ÌïÑÎìú"
          />
        </TextContainer>
      </TypingArea>
      <CompletionMessage show={completed}>
        <h2>üéâ Ï∂ïÌïòÌï©ÎãàÎã§! üéâ</h2>
        <p>ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏãúÎ•º ÏôÑÏÑ±ÌïòÏÖ®ÏäµÎãàÎã§!</p>
      </CompletionMessage>
    </AppContainer>
  );
};

export default App;
