  return false;
};

const App: React.FC = () => {
  const [userInput, setUserInput] = useState('');
  const [completed, setCompleted] = useState(false);
  const [composing, setComposing] = useState(false);
  const [currentText, setCurrentText] = useState('');
  const [debug, setDebug] = useState({ event: '', data: '' });
  const inputRef = useRef<HTMLInputElement>(null);
  const currentPoem = poems[0];

  const calculateProgress = () => {
    if (userInput.length === 0) return 0;
    const correctChars = userInput.split('').filter((char, i) => char === currentPoem.content[i]).length;
    return (correctChars / currentPoem.content.length) * 100;
  };

  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.focus();
    }
  }, []);

  // Ïã§Ï†ú ÏûÖÎ†•Ïù¥ ÏùºÏñ¥ÎÇ† ÎïåÎßàÎã§ Ìò∏Ï∂úÎê©ÎãàÎã§
  const handleInput = (e: React.FormEvent<HTMLInputElement>) => {
    setCurrentText(e.currentTarget.value);
    setDebug(prev => ({ ...prev, event: 'input' }));

    if (!composing) {
      setUserInput(e.currentTarget.value);
      if (e.currentTarget.value.length === currentPoem.content.length) {
        const isCorrect = e.currentTarget.value === currentPoem.content;
        if (isCorrect) {
          setCompleted(true);
        }
      }
    }
  };

  // IME Ï°∞Ìï©Ïù¥ ÏãúÏûëÎê† Îïå Ìò∏Ï∂úÎê©ÎãàÎã§
  const handleCompositionStart = (e: React.CompositionEvent<HTMLInputElement>) => {
    setComposing(true);
    setDebug({ event: 'composition start', data: e.data || '' });
  };

  // IME Ï°∞Ìï©Ïù¥ ÏßÑÌñâ Ï§ëÏùº Îïå Ìò∏Ï∂úÎê©ÎãàÎã§
  const handleCompositionUpdate = (e: React.CompositionEvent<HTMLInputElement>) => {
    setCurrentText(e.currentTarget.value);
    setDebug({ event: 'composition update', data: e.data || '' });
  };

  // IME Ï°∞Ìï©Ïù¥ ÏôÑÎ£åÎê† Îïå Ìò∏Ï∂úÎê©ÎãàÎã§
  const handleCompositionEnd = (e: React.CompositionEvent<HTMLInputElement>) => {
    const finalValue = e.currentTarget.value;
    setComposing(false);
    setCurrentText(finalValue);
    setUserInput(finalValue);
    setDebug({ event: 'composition end', data: e.data || '' });
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Backspace' && !composing) {
      e.preventDefault();
      setUserInput(prev => prev.slice(0, -1));
      setCurrentText(prev => prev.slice(0, -1));
    }
  };

  const renderOverlayText = () => {
    const displayText = currentText;
    const completedText = userInput;
    const targetChars = currentPoem.content.split('');

    return targetChars.map((char, i) => {
      let status: 'correct' | 'incorrect' | 'waiting' = 'waiting';
      let displayChar = char;

      if (i < completedText.length && !composing) {
        // ÏôÑÎ£åÎêú ÏûÖÎ†• Ï≤òÎ¶¨ - Î∂ÄÎ∂Ñ ÏùºÏπò ÌóàÏö©
        status = isPartialMatch(char, completedText[i]) ? 'correct' : 'incorrect';
        displayChar = completedText[i];
      } else if (i < displayText.length) {
        // ÌòÑÏû¨ ÏûÖÎ†• Ï§ëÏù∏ Î∂ÄÎ∂Ñ Ï≤òÎ¶¨ - Î∂ÄÎ∂Ñ ÏùºÏπò ÌóàÏö©
        status = isPartialMatch(char, displayText[i]) ? 'correct' : 'incorrect';
        displayChar = displayText[i];
      }

      return (
        <span key={i} style={{ position: 'relative' }}>
          <Char status={status}>
            {displayChar}
          </Char>
        </span>
      );
    });
  };

  const progress = calculateProgress();

  return (
    <AppContainer onClick={() => inputRef.current?.focus()}>
      <Title>Ïãú ÌÉÄÏù¥Ìïë Ïó∞Ïäµ</Title>
      {/* ÎîîÎ≤ÑÍ∑∏ Ï†ïÎ≥¥ ÌëúÏãú */}
      <div style={{ marginBottom: '1rem', textAlign: 'left', fontSize: '0.8rem', color: '#999' }}>
        <div>Ïù¥Î≤§Ìä∏: {debug.event}</div>
        <div>Îç∞Ïù¥ÌÑ∞: {debug.data}</div>
        <div>ÌòÑÏû¨ ÌÖçÏä§Ìä∏: {currentText}</div>
        <div>ÏôÑÎ£åÎêú ÌÖçÏä§Ìä∏: {userInput}</div>
        <div>Ï°∞Ìï© Ï§ë: {composing ? 'true' : 'false'}</div>
      </div>
      <ProgressBar>
        <Progress width={progress} />
      </ProgressBar>
      <TypingArea>
        <TextContainer>
          <BaseText>{currentPoem.content}</BaseText>
          <OverlayText>{renderOverlayText()}</OverlayText>
        </TextContainer>
        <HiddenInput
          ref={inputRef}
          value={currentText}
          onChange={handleInput}
          onCompositionStart={handleCompositionStart}
          onCompositionUpdate={handleCompositionUpdate}
          onCompositionEnd={handleCompositionEnd}
          onKeyDown={handleKeyDown}
          autoFocus
        />
      </TypingArea>
      <CompletionMessage show={completed}>
        <h2>üéâ Ï∂ïÌïòÌï©ÎãàÎã§! üéâ</h2>
        <p>ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏãúÎ•º ÏôÑÏÑ±ÌïòÏÖ®ÏäµÎãàÎã§!</p>
      </CompletionMessage>
    </AppContainer>
  );
};

export default App;
