import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';

const AppContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem;
  text-align: center;
  background-color: #2b2b2b;
  min-height: 100vh;
  color: #fff;
`;

const Title = styled.h1`
  color: #fff;
  margin-bottom: 2rem;
`;

const TypingArea = styled.div`
  margin-bottom: 2rem;
  position: relative;
  text-align: left;
`;

const TextContainer = styled.div`
  position: relative;
  font-size: 1.2rem;
  line-height: 1.8;
  font-family: monospace;
`;

const BaseText = styled.div`
  white-space: pre-wrap;
  color: #666;
  position: relative;
  z-index: 1;
  min-height: 200px;
  font-family: monospace;
  line-height: 1.8em;
`;

const OverlayText = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  white-space: pre-wrap;
  z-index: 2;
  width: 100%;
  font-family: monospace;
  line-height: 1.8em;
`;

const Char = styled.span<{ status: 'correct' | 'incorrect' | 'waiting' }>`
  color: ${props => {
    switch (props.status) {
      case 'correct': return '#fff';
      case 'incorrect': return '#ff4444';
      case 'waiting': return '#666';
    }
  }};
`;

const ProgressBar = styled.div`
  width: 100%;
  height: 4px;
  background-color: #444;
  margin-bottom: 1rem;
  border-radius: 2px;
`;

const Progress = styled.div<{ width: number }>`
  width: ${props => props.width}%;
  height: 100%;
  background-color: ${props => props.width === 100 ? '#4CAF50' : '#666'};
  border-radius: 2px;
  transition: width 0.3s ease;
`;

const CompletionMessage = styled.div<{ show: boolean }>`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background-color: rgba(0, 0, 0, 0.9);
  padding: 2rem;
  border-radius: 8px;
  display: ${props => props.show ? 'block' : 'none'};
  z-index: 100;
  text-align: center;
`;

const HiddenInput = styled.input`
  position: absolute;
  opacity: 0;
  pointer-events: auto;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  z-index: 3;
  cursor: text;
`;

const Cursor = styled.span`
  display: inline-block;
  width: 2px;
