import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';

const AppContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem;
  text-align: center;
  background-color: #2b2b2b;
  min-height: 100vh;
  color: #fff;
`;

const Title = styled.h1`
  color: #fff;
  margin-bottom: 2rem;
`;

const TypingArea = styled.div`
  margin-bottom: 2rem;
  position: relative;
  text-align: left;
`;

const TextContainer = styled.div`
  position: relative;
  font-size: 1.2rem;
  line-height: 1.8;
  font-family: monospace;
`;

const BaseText = styled.div`
  white-space: pre-wrap;
  color: #666;
  position: relative;
  z-index: 1;
  min-height: 200px;
`;

const OverlayText = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  white-space: pre-wrap;
  z-index: 2;
  width: 100%;
`;

const Char = styled.span<{ status: 'correct' | 'incorrect' | 'waiting' }>`
  color: ${props => {
    switch (props.status) {
      case 'correct': return '#fff';
      case 'incorrect': return '#ff4444';
      case 'waiting': return 'transparent';
    }
  }};
`;

const ProgressBar = styled.div`
  width: 100%;
  height: 4px;
  background-color: #444;
  margin-bottom: 1rem;
  border-radius: 2px;
`;

const Progress = styled.div<{ width: number }>`
  width: ${props => props.width}%;
  height: 100%;
  background-color: ${props => props.width === 100 ? '#4CAF50' : '#666'};
  border-radius: 2px;
  transition: width 0.3s ease;
`;

const CompletionMessage = styled.div<{ show: boolean }>`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background-color: rgba(0, 0, 0, 0.9);
  padding: 2rem;
  border-radius: 8px;
  display: ${props => props.show ? 'block' : 'none'};
  z-index: 100;
  text-align: center;
`;

const HiddenInput = styled.input`
  position: absolute;
  opacity: 0;
  pointer-events: auto;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  z-index: 3;
  cursor: text;
`;

const Cursor = styled.span`
  display: inline-block;
  width: 2px;
  height: 1.4em;
  background-color: #fff;
  margin-left: -1px;
  margin-right: 1px;
  vertical-align: middle;
  animation: blink 1s step-end infinite;
  position: relative;

  @keyframes blink {
    from, to {
      opacity: 1;
    }
    50% {
      opacity: 0;
    }
  }
`;

const poems = [
  {
    title: 'ÎåÄÌôî',
    content: `ÏÇ¨ÎûëÏùÑ ÏûÉÍ≥† ÎÇòÎäî Ïì∞ÎÑ§
Ïûò ÏûàÍ±∞Îùº, ÏßßÏïòÎçò Î∞§Îì§ÏïÑ
Ï∞ΩÎ∞ñÏùÑ Îñ†ÎèåÎçò Í≤®Ïö∏ ÏïàÍ∞úÎì§ÏïÑ
ÏïÑÎ¨¥Í≤ÉÎèÑ Î™®Î•¥Îçò Ï¥õÎ∂àÎì§ÏïÑ, Ïûò ÏûàÍ±∞Îùº
Í≥µÌè¨Î•º Í∏∞Îã§Î¶¨Îçò Ìù∞ Ï¢ÖÏù¥Îì§ÏïÑ
ÎßêÏÑ§ÏûÑÏùÑ ÎåÄÏã†ÌïòÎçò ÎààÎ¨ºÎì§ÏïÑ
Ïûò ÏûàÍ±∞Îùº, Îçî Ïù¥ÏÉÅ ÎÇ¥ Í≤ÉÏù¥ ÏïÑÎãå Ïó¥ÎßùÎì§ÏïÑ
Ïû•ÎãòÏ≤òÎüº ÎÇò Ïù¥Ï†ú ÎçîÎì¨Í±∞Î¶¨Î©∞ Î¨∏ÏùÑ Ïû†Í∑∏ÎÑ§
Í∞ÄÏóæÏùÄ ÎÇ¥ ÏÇ¨Îûë ÎπàÏßëÏóê Í∞áÌòîÎÑ§`,
  }
];

// ÌïúÍ∏Ä Ïú†ÎãàÏΩîÎìú Î≤îÏúÑ Ï≤¥ÌÅ¨
const isKorean = (char: string) => {
  const code = char.charCodeAt(0);
  return code >= 0xAC00 && code <= 0xD7A3;
};

// Ï¥àÏÑ± Í≤ÄÏÇ¨
const isChoseong = (char: string) => {
  const code = char.charCodeAt(0);
  return code >= 0x3131 && code <= 0x314E;
};

// Ï¥àÏÑ± ÌÖåÏù¥Î∏î
const CHOSEONG = ['„Ñ±', '„Ñ≤', '„Ñ¥', '„Ñ∑', '„Ñ∏', '„Ñπ', '„ÖÅ', '„ÖÇ', '„ÖÉ', '„ÖÖ', '„ÖÜ', '„Öá', '„Öà', '„Öâ', '„Öä', '„Öã', '„Öå', '„Öç', '„Öé'];

// ÌïúÍ∏Ä ÏûêÎ™® Î∂ÑÎ¶¨
const decompose = (char: string) => {
  const code = char.charCodeAt(0) - 0xAC00;
  if (code < 0 || code > 11171) return null;

  const jong = code % 28;
  const jung = ((code - jong) / 28) % 21;
  const cho = ((code - jong) / 28 - jung) / 21;

  return {
    cho: Math.floor(cho),
    jung: Math.floor(jung),
    jong: Math.floor(jong)
  };
};

// ÌòÑÏû¨ ÏûÖÎ†•Ïù¥ Î™©ÌëúÏùò ÏùºÎ∂ÄÏù∏ÏßÄ ÌôïÏù∏ (Î°úÏßÅ Í∞úÏÑ†)
const isPartialMatch = (target: string, input: string) => {
  // ÌïúÍ∏ÄÏù¥ ÏïÑÎãàÎ©¥ Ï†ïÌôïÌûà ÏùºÏπòÌï¥Ïïº Ìï®
  if (!isKorean(target)) return target === input;

  // ÏôÑÏ†ÑÌûà ÏùºÏπòÌïòÎäî Í≤ΩÏö∞
  if (target === input) return true;

  // Ï¥àÏÑ±Îßå ÏûÖÎ†•Îêú Í≤ΩÏö∞ (Ïòà: Î™©ÌëúÍ∞Ä 'ÏÇ¨', ÏûÖÎ†•Ïù¥ '„ÖÖ')
  if (isChoseong(input)) {
    const targetDecomp = decompose(target);
    if (targetDecomp) {
      return CHOSEONG[targetDecomp.cho] === input;
    }
  }

  // Ï§ëÏÑ±Îßå ÏûÖÎ†•Îêú Í≤ΩÏö∞ (Ï§ëÏÑ± Í≤ÄÏÇ¨ Ìï®Ïàò ÏßÅÏ†ë ÏÇ¨Ïö©)
  const isJungseong = (char: string) => {
    const code = char.charCodeAt(0);
    return code >= 0x314F && code <= 0x3163;
  };

  if (isJungseong(input)) {
    return false; // Ï§ëÏÑ±Îßå ÏûàÎäî Í≤ΩÏö∞Îäî ÏùºÏπòÌïòÏßÄ ÏïäÏùå
  }

  // Ï¢ÖÏÑ±ÍπåÏßÄ ÏûÖÎ†•Îêú Í≤ΩÏö∞ (Ïòà: Î™©ÌëúÍ∞Ä 'Îùº', ÏûÖÎ†•Ïù¥ 'ÎûÑ')
  if (input.length === 1 && isKorean(input)) {
    const inputDecomp = decompose(input);
    const targetDecomp = decompose(target);

    if (inputDecomp && targetDecomp) {
      // Ï¥àÏÑ±Í≥º Ï§ëÏÑ±Ïù¥ ÏùºÏπòÌïòÎ©¥ Î∂ÄÎ∂Ñ ÏùºÏπòÎ°ú Í∞ÑÏ£º
      return inputDecomp.cho === targetDecomp.cho && 
             inputDecomp.jung === targetDecomp.jung;
    }
  }

  return false;
};

const App: React.FC = () => {
  const [userInput, setUserInput] = useState('');
  const [completed, setCompleted] = useState(false);
  const [composing, setComposing] = useState(false);
  const [currentText, setCurrentText] = useState('');
  const [debug, setDebug] = useState({ event: '', data: '' });
  const [currentPosition, setCurrentPosition] = useState(0);
  const [currentLine, setCurrentLine] = useState(0);
  const inputRef = useRef<HTMLInputElement>(null);
  const currentPoem = poems[0];

  // ÏãúÎ•º Ï§Ñ Îã®ÏúÑÎ°ú Î∂ÑÌï†
  const poemLines = currentPoem.content.split('\n');

  const calculateProgress = () => {
    if (userInput.length === 0) return 0;
    const correctChars = userInput.split('').filter((char, i) => char === currentPoem.content[i]).length;
    return (correctChars / currentPoem.content.length) * 100;
  };

  // ÌòÑÏû¨ Ï§ÑÏù¥ ÏôÑÏÑ±ÎêòÏóàÎäîÏßÄ ÌôïÏù∏ÌïòÎäî Ìï®Ïàò (Î°úÍ∑∏ ÏôÑÏ†Ñ Ï†úÍ±∞)
  const checkLineCompletion = () => {
    if (currentLine >= poemLines.length) return;

    // Ï§ÑÎ∞îÍøàÏù¥ Ïù¥ÎØ∏ Ï≤òÎ¶¨ÎêòÏóàÎäîÏßÄ ÌôïÏù∏ (Ï§ëÎ≥µ Ï≤òÎ¶¨ Î∞©ÏßÄ)
    const lineBreaksInInput = (userInput.match(/\n/g) || []).length;
    if (lineBreaksInInput > currentLine) {
      return; // Ïù¥ÎØ∏ Îã§Ïùå Ï§ÑÎ°ú ÎÑòÏñ¥Í∞îÏúºÎ©¥ Ï≤òÎ¶¨ÌïòÏßÄ ÏïäÏùå
    }

    // ÌòÑÏû¨ Ï§ÑÏùò ÏãúÏûë ÏúÑÏπò Í≥ÑÏÇ∞
    let lineStartPosition = 0;
    for (let i = 0; i < currentLine; i++) {
      lineStartPosition += poemLines[i].length + 1; // +1ÏùÄ Ï§ÑÎ∞îÍøà Î¨∏Ïûê
    }

    // ÌòÑÏû¨ Ï§ÑÏùò ÎÇ¥Ïö©
    const lineContent = poemLines[currentLine];

    // ÌòÑÏû¨ Ï§ÑÏóêÏÑú ÏûÖÎ†•Ìïú ÎÇ¥Ïö©
    const currentLineTyped = userInput.slice(lineStartPosition);

    // ÌòÑÏû¨ Ï§ÑÏùÑ ÏôÑÏ†ÑÌûà ÏûÖÎ†•ÌñàÎäîÏßÄ ÌôïÏù∏
    if (currentLineTyped === lineContent && !composing) {
      moveToNextLine();
    }
  };

  // Îã§Ïùå Ï§ÑÎ°ú Ïù¥ÎèôÌïòÎäî Ìï®Ïàò (Î°úÍ∑∏ Ï†úÍ±∞ Î∞è ÏµúÏ†ÅÌôî)
  const moveToNextLine = () => {
    // ÎßàÏßÄÎßâ Ï§ÑÏù¥Î©¥ Ï≤òÎ¶¨ÌïòÏßÄ ÏïäÏùå
    if (currentLine >= poemLines.length - 1) return;

    // Í∞ôÏùÄ Ï§ÑÏùÑ Îã§Ïãú Ï≤òÎ¶¨ÌïòÏßÄ ÏïäÎèÑÎ°ù Ìñâ Î≤àÌò∏ Ï¶ùÍ∞Ä
    const nextLine = currentLine + 1;

    // Îã§Ïùå Ï§ÑÏùò ÏãúÏûë ÏúÑÏπò Í≥ÑÏÇ∞
    let nextLineStartPosition = 0;
    for (let i = 0; i < nextLine; i++) {
      nextLineStartPosition += poemLines[i].length;
      if (i < nextLine - 1) nextLineStartPosition += 1; // Ïù¥Ï†Ñ Ï§ÑÎì§Ïùò Ï§ÑÎ∞îÍøà
    }

    // Ï§ÑÎ∞îÍøàÏù¥ Ïù¥ÎØ∏ ÏûàÎäîÏßÄ ÌôïÏù∏ (Ï§ëÎ≥µ Ï∂îÍ∞Ä Î∞©ÏßÄ)
    let updatedInput = userInput;
    const expectedBreaks = currentLine;
    const actualBreaks = (userInput.match(/\n/g) || []).length;

    if (actualBreaks < expectedBreaks + 1) {
      // Ï§ÑÎ∞îÍøàÏù¥ ÏóÜÏúºÎ©¥ Ï∂îÍ∞Ä (Ï§ëÎ≥µ Î∞©ÏßÄ)
      if (!updatedInput.endsWith('\n')) {
        updatedInput = userInput + '\n';
        setUserInput(updatedInput);
        setCurrentText(updatedInput);
      }
    }

    // ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏ - Îã§Ïùå Ï§ÑÎ°ú Ïù¥Îèô
    setCurrentLine(nextLine);

    // ÌòÑÏû¨ ÏûÖÎ†• ÏúÑÏπò ÏóÖÎç∞Ïù¥Ìä∏ (+1ÏùÄ Ï§ÑÎ∞îÍøà)
    const newPosition = nextLineStartPosition + (nextLine > 0 ? 1 : 0);
    setCurrentPosition(newPosition);

    // Ìè¨Ïª§Ïä§ Ïú†ÏßÄ
    if (inputRef.current) {
      inputRef.current.focus();
    }
  };

  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.focus();
    }
  }, []);

  // useEffect ÏùòÏ°¥ÏÑ± Î∞∞Ïó¥ ÏàòÏ†ï
  useEffect(() => {
    // Ï°∞Ìï© Ï§ëÏùº ÎïåÎäî Ï≤¥ÌÅ¨ÌïòÏßÄ ÏïäÏùå
    if (!composing) {
      checkLineCompletion();
    }
  }, [userInput, composing, currentLine, poemLines]); // checkLineCompletion Ï†úÍ±∞

  // ÏûÖÎ†• Ï≤òÎ¶¨ Ìï®Ïàò
  const handleInput = (e: React.FormEvent<HTMLInputElement>) => {
    e.preventDefault();

    const value = e.currentTarget?.value || '';
    const selectionStart = e.currentTarget?.selectionStart ?? 0;

    setDebug({
      event: 'input',
      data: value + ' / cursor: ' + selectionStart
    });

    if (!composing) {
      setUserInput(value);
      setCurrentPosition(selectionStart);

      // ÌÉÄÏù¥Ìïë ÏôÑÎ£å Ï≤¥ÌÅ¨
      if (value === currentPoem.content) {
        setCompleted(true);
      }
    }
  };

  // IME Ï°∞Ìï©Ïù¥ ÏãúÏûëÎê† Îïå Ìò∏Ï∂úÎê©ÎãàÎã§
  const handleCompositionStart = (e: React.CompositionEvent<HTMLInputElement>) => {
    setComposing(true);
    setDebug({ event: 'composition start', data: e.data || '' });
  };

  // IME Ï°∞Ìï©Ïù¥ ÏßÑÌñâ Ï§ëÏùº Îïå Ìò∏Ï∂úÎê©ÎãàÎã§
  const handleCompositionUpdate = (e: React.CompositionEvent<HTMLInputElement>) => {
    setCurrentText(e.currentTarget.value);
    setDebug({ event: 'composition update', data: e.data || '' });
  };

  // IME Ï°∞Ìï©Ïù¥ ÏôÑÎ£åÎê† Îïå Ìò∏Ï∂úÎê©ÎãàÎã§
  const handleCompositionEnd = (e: React.CompositionEvent<HTMLInputElement>) => {
    const finalValue = e.currentTarget.value;
    setComposing(false);
    setCurrentText(finalValue);
    setUserInput(finalValue);
    setCurrentPosition(finalValue.length);
    setDebug({ event: 'composition end', data: e.data || '' });

    // Ï°∞Ìï©Ïù¥ ÎÅùÎÇú ÌõÑ ÏûêÎèôÏúºÎ°ú Ï§Ñ ÏôÑÏÑ± Ï≤¥ÌÅ¨
    setTimeout(() => {
      checkLineCompletion();
    }, 0);
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Backspace' && !composing) {
      e.preventDefault();
      setUserInput(prev => prev.slice(0, -1));
      setCurrentText(prev => prev.slice(0, -1));
      setCurrentPosition(prev => Math.max(0, prev - 1));
    }
  };

  // Î†åÎçîÎßÅ Ìï®Ïàò ÏàòÏ†ï
  const renderOverlayText = () => {
    const displayText = currentText;
    const completedText = userInput;
    const targetChars = currentPoem.content.split('');

    return targetChars.map((char, i) => {
      let status: 'correct' | 'incorrect' | 'waiting' = 'waiting';
      let displayChar = char;
      const showCursor = i === currentPosition;
      const isCharEntered = i < completedText.length || (composing && i === currentPosition);

      // Ï§ÑÎ∞îÍøà Î¨∏Ïûê Ï≤òÎ¶¨
      if (char === '\n') {
        let lineBreakStatus = 'waiting';

        // Ïù¥ÎØ∏ ÏûÖÎ†•Îêú Ï§ÑÎ∞îÍøà
        if (i < completedText.length && completedText[i] === '\n') {
          lineBreakStatus = 'correct';
        }

        return (
          <span key={i} style={{ position: 'relative', display: 'block', height: '1.8em' }}>
            {showCursor && !isCharEntered && <Cursor />}
            <Char status={lineBreakStatus as 'correct' | 'incorrect' | 'waiting'}>
              <br />
            </Char>
            {showCursor && isCharEntered && <Cursor />}
          </span>
        );
      }

      // Í∏∞Î≥∏ ÏÉÅÌÉúÎäî 'ÎåÄÍ∏∞ Ï§ë'
      status = 'waiting';

      // Ïù¥ÎØ∏ ÏûÖÎ†• ÏôÑÎ£åÎêú Í∏ÄÏûê Ï≤òÎ¶¨
      if (i < completedText.length) {
        // ÏûÖÎ†•Îêú ÌÖçÏä§Ìä∏ÏôÄ ÌÉÄÍ≤ü ÌÖçÏä§Ìä∏ ÎπÑÍµê
        if (isPartialMatch(char, completedText[i])) {
          status = 'correct';
        } else {
          status = 'incorrect';
        }
        displayChar = completedText[i];
      } 
      // ÌòÑÏû¨ Ï°∞Ìï© Ï§ëÏù∏ Í∏ÄÏûê Ï≤òÎ¶¨ (IME Ï°∞Ìï© Ï§ë)
      else if (composing && i === currentPosition) {
        // Ï°∞Ìï© Ï§ëÏù∏ Í∏ÄÏûêÎäî ÌÖçÏä§Ìä∏ ÏûÖÎ†•Ïùò ÎßàÏßÄÎßâ Í∏ÄÏûê
        if (displayText.length > completedText.length) {
          const composedChar = displayText.substring(completedText.length);
          if (composedChar) {
            if (isPartialMatch(char, composedChar)) {
              status = 'correct';
            } else {
              status = 'incorrect';
            }
            displayChar = composedChar;
          }
        }
      }

      return (
        <span key={i} style={{ position: 'relative' }}>
          {showCursor && !isCharEntered && <Cursor />}
          <Char status={status}>
            {displayChar}
          </Char>
          {showCursor && isCharEntered && <Cursor />}
        </span>
      );
    });
  };

  const progress = calculateProgress();

  // ÏûÖÎ†• ÌïÑÎìúÏóê Ìè¨Ïª§Ïä§ Ïú†ÏßÄÎ•º ÏúÑÌïú Ìö®Í≥º Ï∂îÍ∞Ä
  useEffect(() => {
    const focusInterval = setInterval(() => {
      if (inputRef.current && document.activeElement !== inputRef.current) {
        inputRef.current.focus();
      }
    }, 500);

    return () => clearInterval(focusInterval);
  }, []);

  // Ï¥àÍ∏∞Ìôî Ïãú Ïª§ÏÑú ÏúÑÏπòÎ•º 0ÏúºÎ°ú ÏÑ§Ï†ï
  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.focus();
      setCurrentPosition(0);
    }
  }, []); // ÏùòÏ°¥ÏÑ± Î∞∞Ïó¥ ÎπÑÏõåÎë†

  useEffect(() => {
    checkLineCompletion();
  }, [currentLine, currentText, checkLineCompletion]);

  return (
    <AppContainer onClick={() => inputRef.current?.focus()}>
      <Title>Ïãú ÌÉÄÏù¥Ìïë Ïó∞Ïäµ</Title>
      {/* ÎîîÎ≤ÑÍ∑∏ Ï†ïÎ≥¥ ÌëúÏãú (Í∞úÎ∞ú Î™®ÎìúÏóêÏÑúÎßå) */}
      {process.env.NODE_ENV === 'development' && (
        <div className="debug">
          <h3>ÎîîÎ≤ÑÍ∑∏ Ï†ïÎ≥¥:</h3>
          <pre>{JSON.stringify(debug, null, 2)}</pre>
        </div>
      )}
      {/* ÎîîÎ≤ÑÍ∑∏ Ï†ïÎ≥¥ ÌëúÏãú (Í∞úÎ∞ú Î™®ÎìúÏóêÏÑúÎßå) */}
      {window.location.hostname === 'localhost' && (
        <div className="debug">
          <h3>ÎîîÎ≤ÑÍ∑∏ Ï†ïÎ≥¥:</h3>
          <pre>{JSON.stringify(debug, null, 2)}</pre>
        </div>
      )}
      <ProgressBar>
        <Progress width={progress} />
      </ProgressBar>
      <TypingArea>
        <TextContainer>
          <BaseText>{currentPoem.content}</BaseText>
          <OverlayText>{renderOverlayText()}</OverlayText>
        </TextContainer>
        <HiddenInput
          ref={inputRef}
          value={currentText}
          onChange={handleInput}
          onCompositionStart={handleCompositionStart}
          onCompositionUpdate={handleCompositionUpdate}
          onCompositionEnd={handleCompositionEnd}
          onKeyDown={handleKeyDown}
          autoFocus
        />
      </TypingArea>
