import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';

const AppContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem;
  text-align: center;
  background-color: #2b2b2b;
  min-height: 100vh;
  color: #fff;
`;

const Title = styled.h1`
  color: #fff;
  margin-bottom: 2rem;
`;

const TypingArea = styled.div`
  margin-bottom: 2rem;
  position: relative;
  text-align: left;
`;

const TextContainer = styled.div`
  position: relative;
  font-size: 1.2rem;
  line-height: 1.8;
  font-family: monospace;
`;

const BaseText = styled.div`
  white-space: pre-wrap;
  color: #666;
  position: relative;
  z-index: 1;
  min-height: 200px;
`;

const OverlayText = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  white-space: pre-wrap;
  z-index: 2;
  width: 100%;
`;

const Char = styled.span<{ status: 'correct' | 'incorrect' | 'waiting' }>`
  color: ${props => {
    switch (props.status) {
      case 'correct': return '#fff';
      case 'incorrect': return '#ff4444';
      case 'waiting': return 'transparent';
    }
  }};
`;

const ComposingChar = styled.span`
  position: absolute;
  color: #fff;
  background: transparent;
`;

const ProgressBar = styled.div`
  width: 100%;
  height: 4px;
  background-color: #444;
  margin-bottom: 1rem;
  border-radius: 2px;
`;

const Progress = styled.div<{ width: number }>`
  width: ${props => props.width}%;
  height: 100%;
  background-color: ${props => props.width === 100 ? '#4CAF50' : '#666'};
  border-radius: 2px;
  transition: width 0.3s ease;
`;

const CompletionMessage = styled.div<{ show: boolean }>`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background-color: rgba(0, 0, 0, 0.9);
  padding: 2rem;
  border-radius: 8px;
  display: ${props => props.show ? 'block' : 'none'};
  z-index: 100;
  text-align: center;
`;

const HiddenInput = styled.input`
  position: absolute;
  opacity: 0;
  pointer-events: none;
`;

const Cursor = styled.span`
  display: inline-block;
  width: 2px;
  height: 1.4em;
  background-color: #fff;
  margin-left: -1px;
  margin-right: 1px;
  vertical-align: middle;
  animation: blink 1s step-end infinite;
  position: relative;

  @keyframes blink {
    from, to {
      opacity: 1;
    }
    50% {
      opacity: 0;
    }
  }
`;

const poems = [
  {
    title: '대화',
    content: `사랑을 잃고 나는 쓰네
잘 있거라, 짧았던 밤들아
창밖을 떠돌던 겨울 안개들아
아무것도 모르던 촛불들아, 잘 있거라
공포를 기다리던 흰 종이들아
말설임을 대신하던 눈물들아
잘 있거라, 더 이상 내 것이 아닌 열망들아
장님처럼 나 이제 더듬거리며 문을 잠그네
가엾은 내 사랑 빈집에 갇혔네`,
  }
];

// 한글 유니코드 범위 체크
const isKorean = (char: string) => {
  const code = char.charCodeAt(0);
  return code >= 0xAC00 && code <= 0xD7A3;
};

// 초성 검사
const isChoseong = (char: string) => {
  const code = char.charCodeAt(0);
  return code >= 0x3131 && code <= 0x314E;
};

// 중성 검사
const isJungseong = (char: string) => {
  const code = char.charCodeAt(0);
  return code >= 0x314F && code <= 0x3163;
};

// 초성 테이블
const CHOSEONG = ['ㄱ', 'ㄲ', 'ㄴ', 'ㄷ', 'ㄸ', 'ㄹ', 'ㅁ', 'ㅂ', 'ㅃ', 'ㅅ', 'ㅆ', 'ㅇ', 'ㅈ', 'ㅉ', 'ㅊ', 'ㅋ', 'ㅌ', 'ㅍ', 'ㅎ'];

// 한글 자모 분리
const decompose = (char: string) => {
  const code = char.charCodeAt(0) - 0xAC00;
  if (code < 0 || code > 11171) return null;

  const jong = code % 28;
  const jung = ((code - jong) / 28) % 21;
  const cho = ((code - jong) / 28 - jung) / 21;

  return {
    cho: Math.floor(cho),
    jung: Math.floor(jung),
    jong: Math.floor(jong)
  };
};

// 현재 입력이 목표의 일부인지 확인 (로직 개선)
const isPartialMatch = (target: string, input: string) => {
  // 한글이 아니면 정확히 일치해야 함
  if (!isKorean(target)) return target === input;

  // 완전히 일치하는 경우
  if (target === input) return true;

  // 초성만 입력된 경우 (예: 목표가 '사', 입력이 'ㅅ')
  if (isChoseong(input)) {
    const targetDecomp = decompose(target);
    if (targetDecomp) {
      return CHOSEONG[targetDecomp.cho] === input;
    }
  }

  // 종성까지 입력된 경우 (예: 목표가 '라', 입력이 '랄')
  if (input.length === 1 && isKorean(input)) {
    const inputDecomp = decompose(input);
    const targetDecomp = decompose(target);

    if (inputDecomp && targetDecomp) {
      // 초성과 중성이 일치하면 부분 일치로 간주
      return inputDecomp.cho === targetDecomp.cho && 
             inputDecomp.jung === targetDecomp.jung;
    }
  }

  return false;
};

const App: React.FC = () => {
  const [userInput, setUserInput] = useState('');
  const [completed, setCompleted] = useState(false);
  const [composing, setComposing] = useState(false);
  const [currentText, setCurrentText] = useState('');
  const [debug, setDebug] = useState({ event: '', data: '' });
  const [currentPosition, setCurrentPosition] = useState(0);
  const [currentLine, setCurrentLine] = useState(0);
  const inputRef = useRef<HTMLInputElement>(null);
  const currentPoem = poems[0];

  // 시를 줄 단위로 분할
  const poemLines = currentPoem.content.split('\n');

  const calculateProgress = () => {
    if (userInput.length === 0) return 0;
    const correctChars = userInput.split('').filter((char, i) => char === currentPoem.content[i]).length;
    return (correctChars / currentPoem.content.length) * 100;
  };

  // 다음 줄로 이동하는 함수
  const moveToNextLine = () => {
    if (currentLine < poemLines.length - 1) {
      // 현재 줄의 끝 위치 계산
      let currentLineEndPosition = 0;
      for (let i = 0; i <= currentLine; i++) {
        currentLineEndPosition += poemLines[i].length;
        if (i < currentLine) currentLineEndPosition += 1; // 이전 줄들의 줄바꿈 문자
      }

      // 현재 입력에 줄바꿈 추가
      if (userInput.length === currentLineEndPosition) {
        const updatedInput = userInput + '\n';
        setUserInput(updatedInput);
        setCurrentText(updatedInput);

        // 다음 줄로 이동
        const nextLine = currentLine + 1;
        setCurrentLine(nextLine);

        // 다음 줄의 시작 위치 계산
        let position = 0;
        for (let i = 0; i < nextLine; i++) {
          position += poemLines[i].length + 1; // +1은 줄바꿈 문자
        }

        // 현재 입력 위치 업데이트
        setCurrentPosition(position);

        // 포커스 유지
        if (inputRef.current) {
          inputRef.current.focus();
        }
      }
    }
  };

  // 현재 줄이 완성되었는지 확인하는 함수
  const checkLineCompletion = () => {
    if (currentLine >= poemLines.length) return;

    // 현재 줄의 시작 위치 계산
    let lineStartPosition = 0;
    for (let i = 0; i < currentLine; i++) {
      lineStartPosition += poemLines[i].length + 1; // +1은 줄바꿈 문자
    }

    // 현재 줄의 내용과 길이
    const lineContent = poemLines[currentLine];
    const currentLineEndPosition = lineStartPosition + lineContent.length;

    // 사용자가 입력한 현재 줄의 내용
    let currentLineInput = '';
    if (userInput.length >= lineStartPosition) {
      currentLineInput = userInput.substring(
        lineStartPosition,
        Math.min(userInput.length, currentLineEndPosition)
      );
    }

    // 현재 줄이 완료되었는지 확인
    const isCurrentLineCompleted = currentLineInput === lineContent;

    // 현재 줄을 정확히 입력했고, 조합 중이 아니면 다음 줄로 이동
    if (isCurrentLineCompleted && !composing && userInput.length >= currentLineEndPosition) {
      moveToNextLine();
    }
  };

  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.focus();
    }
  }, []);

  // 입력 위치가 변경될 때마다 라인 완성 체크
  useEffect(() => {
    checkLineCompletion();
  }, [userInput, composing]);

  // 입력 처리 함수 수정
  const handleInput = (e: React.FormEvent<HTMLInputElement>) => {
    const newValue = e.currentTarget.value;
    setCurrentText(newValue);
    setDebug(prev => ({ ...prev, event: 'input', value: newValue }));

    if (!composing) {
      setUserInput(newValue);
      setCurrentPosition(newValue.length);

      if (newValue.length === currentPoem.content.length) {
        const isCorrect = newValue === currentPoem.content;
        if (isCorrect) {
          setCompleted(true);
        }
      }
    }
  };

  // IME 조합이 시작될 때 호출됩니다
  const handleCompositionStart = (e: React.CompositionEvent<HTMLInputElement>) => {
    setComposing(true);
    setDebug({ event: 'composition start', data: e.data || '' });
  };

  // IME 조합이 진행 중일 때 호출됩니다
  const handleCompositionUpdate = (e: React.CompositionEvent<HTMLInputElement>) => {
    setCurrentText(e.currentTarget.value);
    setDebug({ event: 'composition update', data: e.data || '' });
  };

  // IME 조합이 완료될 때 호출됩니다
  const handleCompositionEnd = (e: React.CompositionEvent<HTMLInputElement>) => {
    const finalValue = e.currentTarget.value;
    setComposing(false);
    setCurrentText(finalValue);
    setUserInput(finalValue);
    setCurrentPosition(finalValue.length);
    setDebug({ event: 'composition end', data: e.data || '' });
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Backspace' && !composing) {
      e.preventDefault();
      setUserInput(prev => prev.slice(0, -1));
      setCurrentText(prev => prev.slice(0, -1));
      setCurrentPosition(prev => Math.max(0, prev - 1));
    }
  };

  // 렌더링 함수 수정
  const renderOverlayText = () => {
    const displayText = currentText;
    const completedText = userInput;
    const targetChars = currentPoem.content.split('');
    const cursorPosition = currentPosition;

    return targetChars.map((char, i) => {
      let status: 'correct' | 'incorrect' | 'waiting' = 'waiting';
      let displayChar = char;
      const showCursor = i === cursorPosition;

      // 줄바꿈 문자 처리
      if (char === '\n') {
        let lineBreakStatus = 'waiting';
        if (i < completedText.length && completedText[i] === '\n') {
          lineBreakStatus = 'correct';
        }

        return (
          <span key={i} style={{ position: 'relative', display: 'block', height: '1.8em' }}>
            <Char status={lineBreakStatus as 'correct' | 'incorrect' | 'waiting'}>
              <br />
            </Char>
            {showCursor && <Cursor />}
          </span>
        );
      }

      // 기본 상태는 '대기 중'
      status = 'waiting';

      // 이미 입력 완료된 글자 처리
      if (i < completedText.length) {
        // 입력된 텍스트와 타겟 텍스트 비교
        if (isPartialMatch(char, completedText[i])) {
          status = 'correct';
        } else {
          status = 'incorrect';
        }
        displayChar = completedText[i];
      } 
      // 현재 조합 중인 글자 처리 (IME 조합 중)
      else if (composing && i === cursorPosition) {
        // 조합 중인 글자는 텍스트 입력의 마지막 글자
        if (displayText.length > completedText.length) {
          const composedChar = displayText.substring(completedText.length);
          if (composedChar) {
            if (isPartialMatch(char, composedChar)) {
              status = 'correct';
            } else {
              status = 'incorrect';
            }
            displayChar = composedChar;
          }
        }
      }

      return (
        <span key={i} style={{ position: 'relative' }}>
          <Char status={status}>
            {displayChar}
          </Char>
          {showCursor && <Cursor />}
        </span>
      );
    });
  };

  const progress = calculateProgress();

  // 입력 필드에 포커스 유지를 위한 효과 추가
  useEffect(() => {
    const focusInterval = setInterval(() => {
      if (inputRef.current && document.activeElement !== inputRef.current) {
        inputRef.current.focus();
      }
    }, 500);

    return () => clearInterval(focusInterval);
  }, []);

  return (
    <AppContainer onClick={() => inputRef.current?.focus()}>
      <Title>시 타이핑 연습</Title>
      {/* 디버그 정보 표시 */}
      <div style={{ marginBottom: '1rem', textAlign: 'left', fontSize: '0.8rem', color: '#999' }}>
        <div>이벤트: {debug.event}</div>
        <div>데이터: {debug.data}</div>
        <div>현재 텍스트: {currentText}</div>
        <div>완료된 텍스트: {userInput}</div>
        <div>조합 중: {composing ? 'true' : 'false'}</div>
        <div>현재 줄: {currentLine + 1}</div>
        <div>현재 위치: {currentPosition}</div>
      </div>
      <ProgressBar>
        <Progress width={progress} />
      </ProgressBar>
      <TypingArea>
        <TextContainer>
          <BaseText>{currentPoem.content}</BaseText>
          <OverlayText>{renderOverlayText()}</OverlayText>
        </TextContainer>
        <HiddenInput
          ref={inputRef}
          value={currentText}
          onChange={handleInput}
          onCompositionStart={handleCompositionStart}
          onCompositionUpdate={handleCompositionUpdate}
          onCompositionEnd={handleCompositionEnd}
          onKeyDown={handleKeyDown}
          autoFocus
        />
      </TypingArea>
      <CompletionMessage show={completed}>
        <h2>🎉 축하합니다! 🎉</h2>
        <p>성공적으로 시를 완성하셨습니다!</p>
      </CompletionMessage>
    </AppContainer>
  );
};

export default App;
