const poems = [
  {
    title: '대화',
    content: `사랑을 잃고 나는 쓰네
잘 있거라, 짧았던 밤들아
창밖을 떠돌던 겨울 안개들아
아무것도 모르던 촛불들아, 잘 있거라
공포를 기다리던 흰 종이들아
말설임을 대신하던 눈물들아
잘 있거라, 더 이상 내 것이 아닌 열망들아
장님처럼 나 이제 더듬거리며 문을 잠그네
가엾은 내 사랑 빈집에 갇혔네`,
  }
];

// 한글 유니코드 범위 체크
const isKorean = (char: string) => {
  const code = char.charCodeAt(0);
  return code >= 0xAC00 && code <= 0xD7A3;
};

// 초성 검사
const isChoseong = (char: string) => {
  const code = char.charCodeAt(0);
  return code >= 0x3131 && code <= 0x314E;
};

// 초성 테이블
const CHOSEONG = ['ㄱ', 'ㄲ', 'ㄴ', 'ㄷ', 'ㄸ', 'ㄹ', 'ㅁ', 'ㅂ', 'ㅃ', 'ㅅ', 'ㅆ', 'ㅇ', 'ㅈ', 'ㅉ', 'ㅊ', 'ㅋ', 'ㅌ', 'ㅍ', 'ㅎ'];

// 한글 자모 분리
const decompose = (char: string) => {
  const code = char.charCodeAt(0) - 0xAC00;
  if (code < 0 || code > 11171) return null;

  const jong = code % 28;
  const jung = ((code - jong) / 28) % 21;
  const cho = ((code - jong) / 28 - jung) / 21;

  return {
    cho: Math.floor(cho),
    jung: Math.floor(jung),
    jong: Math.floor(jong)
  };
};

// 현재 입력이 목표의 일부인지 확인 (로직 개선)
const isPartialMatch = (target: string, input: string) => {
  // 한글이 아니면 정확히 일치해야 함
  if (!isKorean(target)) return target === input;

  // 완전히 일치하는 경우
  if (target === input) return true;

  // 초성만 입력된 경우 (예: 목표가 '사', 입력이 'ㅅ')
  if (isChoseong(input)) {
    const targetDecomp = decompose(target);
    if (targetDecomp) {
      return CHOSEONG[targetDecomp.cho] === input;
    }
  }

  // 중성만 입력된 경우 (중성 검사 함수 직접 사용)
  const isJungseong = (char: string) => {
    const code = char.charCodeAt(0);
    return code >= 0x314F && code <= 0x3163;
  };

  if (isJungseong(input)) {
    return false; // 중성만 있는 경우는 일치하지 않음
  }

  // 종성까지 입력된 경우 (예: 목표가 '라', 입력이 '랄')
  if (input.length === 1 && isKorean(input)) {
    const inputDecomp = decompose(input);
    const targetDecomp = decompose(target);

    if (inputDecomp && targetDecomp) {
      // 초성과 중성이 일치하면 부분 일치로 간주
      return inputDecomp.cho === targetDecomp.cho && 
             inputDecomp.jung === targetDecomp.jung;
    }
  }

  return false;
};

const App: React.FC = () => {
  const [userInput, setUserInput] = useState('');
  const [completed, setCompleted] = useState(false);
  const [composing, setComposing] = useState(false);
  const [currentText, setCurrentText] = useState('');
  const [debug, setDebug] = useState({ event: '', data: '' });
  const [currentPosition, setCurrentPosition] = useState(0);
  const [currentLine, setCurrentLine] = useState(0);
  const inputRef = useRef<HTMLInputElement>(null);
  const currentPoem = poems[0];

  // 시를 줄 단위로 분할
  const poemLines = currentPoem.content.split('\n');

  const calculateProgress = () => {
    if (userInput.length === 0) return 0;
    const correctChars = userInput.split('').filter((char, i) => char === currentPoem.content[i]).length;
    return (correctChars / currentPoem.content.length) * 100;
  };

  // 현재 줄이 완성되었는지 확인하는 함수 (로그 완전 제거)
  const checkLineCompletion = () => {
    // 자동 줄바꿈 비활성화
    return;
  };

  // 다음 줄로 이동하는 함수 (로그 제거 및 최적화)
  const moveToNextLine = () => {
    // 마지막 줄이면 처리하지 않음
    if (currentLine >= poemLines.length - 1) return;

    // 같은 줄을 다시 처리하지 않도록 행 번호 증가
    const nextLine = currentLine + 1;

    // 다음 줄의 시작 위치 계산
    let nextLineStartPosition = 0;
    for (let i = 0; i < nextLine; i++) {
      nextLineStartPosition += poemLines[i].length;
      if (i < nextLine - 1) nextLineStartPosition += 1; // 이전 줄들의 줄바꿈
    }

    // 줄바꿈이 이미 있는지 확인 (중복 추가 방지)
    let updatedInput = userInput;
    const expectedBreaks = currentLine;
    const actualBreaks = (userInput.match(/\n/g) || []).length;

    if (actualBreaks < expectedBreaks + 1) {
      // 줄바꿈이 없으면 추가 (중복 방지)
      if (!updatedInput.endsWith('\n')) {
        updatedInput = userInput + '\n';
        setUserInput(updatedInput);
        setCurrentText(updatedInput);
      }
    }

    // 상태 업데이트 - 다음 줄로 이동
    setCurrentLine(nextLine);

    // 현재 입력 위치 업데이트 (+1은 줄바꿈)
    const newPosition = nextLineStartPosition + (nextLine > 0 ? 1 : 0);
    setCurrentPosition(newPosition);

    // 포커스 유지
    if (inputRef.current) {
      inputRef.current.focus();
    }
  };

  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.focus();
    }
  }, []);

  // useEffect 의존성 배열 수정
  useEffect(() => {
    // 조합 중일 때는 체크하지 않음
    if (!composing) {
      checkLineCompletion();
    }
  }, [userInput, composing, currentLine, poemLines]); // checkLineCompletion 제거

  // 입력 처리 함수
  const handleInput = (e: React.FormEvent<HTMLInputElement>) => {
    e.preventDefault();

    const value = e.currentTarget?.value || '';
    const selectionStart = e.currentTarget?.selectionStart ?? 0;

    setDebug({
      event: 'input',
      data: value + ' / cursor: ' + selectionStart + ' / composing: ' + composing
    });

    // 항상 현재 텍스트 업데이트
    setCurrentText(value);

    if (!composing) {
      setUserInput(value);
      // 위치를 현재 입력 길이로 설정하여 다음 글자 위치에 커서 표시
      setCurrentPosition(value.length);

      // 타이핑 완료 체크
      if (value === currentPoem.content) {
        setCompleted(true);
      }
    }
  };

  // IME 조합이 시작될 때 호출됩니다
  const handleCompositionStart = (e: React.CompositionEvent<HTMLInputElement>) => {
    setComposing(true);
    setDebug({ 
      event: 'composition start', 
      data: e.data || '' + ' / value: ' + e.currentTarget.value 
    });
  };

  // IME 조합이 진행 중일 때 호출됩니다
  const handleCompositionUpdate = (e: React.CompositionEvent<HTMLInputElement>) => {
    const value = e.currentTarget.value;
    setCurrentText(value);
    setDebug({ 
      event: 'composition update', 
      data: e.data || '' + ' / value: ' + value
    });
  };

  // IME 조합이 완료될 때 호출됩니다
  const handleCompositionEnd = (e: React.CompositionEvent<HTMLInputElement>) => {
    const finalValue = e.currentTarget.value;
    setComposing(false);
    setCurrentText(finalValue);
    setUserInput(finalValue);
    // 커서 위치 설정 - 입력 길이로 설정
    setCurrentPosition(finalValue.length);
    setDebug({ 
      event: 'composition end', 
      data: e.data || '' + ' / value: ' + finalValue
    });

    // 자동 줄바꿈 비활성화
    // setTimeout(() => {
    //   checkLineCompletion();
    // }, 0);
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    // 백스페이스 처리
    if (e.key === 'Backspace' && !composing) {
      e.preventDefault();
      const newInput = currentText.slice(0, -1);
      setCurrentText(newInput);
      setUserInput(newInput);
      // 삭제 후 커서 위치 업데이트
      setCurrentPosition(newInput.length);
    }

    // Enter 키 처리
    if (e.key === 'Enter' && !composing) {
      e.preventDefault();

      // 원문의 현재 위치에 줄바꿈이 있는지 확인
      const poem = currentPoem.content;

      // 줄바꿈이 있다면 그냥 입력
      if (currentPosition < poem.length && poem[currentPosition] === '\n') {
        const newInput = currentText + '\n';
        setCurrentText(newInput);
        setUserInput(newInput);
        setCurrentPosition(newInput.length);
      }
    }
  };

  // 렌더링 함수 수정
  const renderOverlayText = () => {
    const overlayContent = [];
    const targetChars = currentPoem.content.split('');
    const inputChars = currentText.split('');

    // 기존의 각 문자별 처리 방식 대신, 원문 텍스트 위에 입력 텍스트를 그대로 표시
    for (let i = 0; i < targetChars.length; i++) {
      const targetChar = targetChars[i];
      const inputChar = i < inputChars.length ? inputChars[i] : '';
      const showCursor = i === currentPosition;

      // 줄바꿈 문자 처리
      if (targetChar === '\n') {
        overlayContent.push(
          <span key={i} style={{ position: 'relative', display: 'block', height: '1.8em' }}>
            {i < inputChars.length && inputChars[i] === '\n' && (
              <Char status="correct">
                <br />
              </Char>
            )}
            {showCursor && <Cursor />}
          </span>
        );
        continue;
      }

      // 입력된 글자 처리
      if (i < inputChars.length) {
        let status: 'correct' | 'incorrect' = isPartialMatch(targetChar, inputChar) ? 'correct' : 'incorrect';

        overlayContent.push(
          <span key={i} style={{ position: 'relative' }}>
            <Char status={status}>
              {inputChar}
            </Char>
            {showCursor && <Cursor />}
          </span>
        );
      } 
      // 아직 입력되지 않은 글자는 커서만 표시
      else {
        overlayContent.push(
          <span key={i} style={{ position: 'relative' }}>
            {showCursor && <Cursor />}
          </span>
        );
      }
    }

    return overlayContent;
  };

  const progress = calculateProgress();

  // 입력 필드에 포커스 유지를 위한 효과 추가
  useEffect(() => {
    const focusInterval = setInterval(() => {
      if (inputRef.current && document.activeElement !== inputRef.current) {
        inputRef.current.focus();
      }
    }, 500);

    return () => clearInterval(focusInterval);
  }, []);

  // 초기화 시 커서 위치를 0으로 설정
  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.focus();
      setCurrentPosition(0);
    }
  }, []); // 의존성 배열 비워둠

  useEffect(() => {
    checkLineCompletion();
  }, [currentLine, currentText, checkLineCompletion]);

  return (
    <AppContainer onClick={() => inputRef.current?.focus()}>
      <Title>시 타이핑 연습</Title>
      {/* 디버그 정보 표시 (개발 모드에서만) */}
      {process.env.NODE_ENV === 'development' && (
        <div className="debug">
          <h3>디버그 정보:</h3>
          <pre>{JSON.stringify(debug, null, 2)}</pre>
        </div>
      )}
      {/* 디버그 정보 표시 (개발 모드에서만) */}
      {window.location.hostname === 'localhost' && (
        <div className="debug">
          <h3>디버그 정보:</h3>
          <pre>{JSON.stringify(debug, null, 2)}</pre>
        </div>
      )}
      <ProgressBar>
        <Progress width={progress} />
      </ProgressBar>
      <TypingArea>
        <TextContainer>
          <BaseText>{currentPoem.content}</BaseText>
          <OverlayText>{renderOverlayText()}</OverlayText>
        </TextContainer>
        <HiddenInput
          ref={inputRef}
          value={currentText}
          onChange={handleInput}
          onCompositionStart={handleCompositionStart}
          onCompositionUpdate={handleCompositionUpdate}
          onCompositionEnd={handleCompositionEnd}
          onKeyDown={handleKeyDown}
          autoFocus
          autoComplete="off"
          autoCorrect="off"
          spellCheck={false}
          aria-label="시 입력 필드"
        />
      </TypingArea>
      <CompletionMessage show={completed}>
        <h2>🎉 축하합니다! 🎉</h2>
        <p>성공적으로 시를 완성하셨습니다!</p>
      </CompletionMessage>
    </AppContainer>
  );
};

export default App;
