import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const AppContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem;
  text-align: center;
`;

const Title = styled.h1`
  color: #333;
  margin-bottom: 2rem;
`;

const TypingArea = styled.div`
  margin-bottom: 2rem;
`;

const PoemDisplay = styled.div`
  font-size: 1.2rem;
  line-height: 1.8;
  margin-bottom: 1rem;
  white-space: pre-wrap;
  text-align: left;
`;

const TypingInput = styled.textarea`
  width: 100%;
  height: 150px;
  padding: 1rem;
  font-size: 1.1rem;
  border: 2px solid #ddd;
  border-radius: 8px;
  resize: none;
  &:focus {
    outline: none;
    border-color: #666;
  }
`;

const PoemSelector = styled.select`
  width: 100%;
  padding: 0.5rem;
  margin-bottom: 1rem;
  font-size: 1rem;
  border: 1px solid #ddd;
  border-radius: 4px;
`;

const AccuracyDisplay = styled.div`
  margin-top: 1rem;
  font-size: 1.1rem;
  color: #666;
`;

const HighlightedText = styled.span<{ isCorrect: boolean }>`
  background-color: ${props => props.isCorrect ? 'transparent' : '#ffebee'};
  color: ${props => props.isCorrect ? 'inherit' : '#d32f2f'};
`;

interface Poem {
  title: string;
  author: string;
  content: string;
}

const poems: Poem[] = [
  {
    title: '봄날',
    author: '김소월',
    content: `봄날은 간다
무심히 간다
머릿속 그림자조차
없이 간다`
  },
  {
    title: '진달래꽃',
    author: '김소월',
    content: `나 보기가 역겨워
가실 때에는
말없이 고이 보내드리오리다`
  },
  {
    title: '별 헤는 밤',
    author: '윤동주',
    content: `계절이 지나가는 하늘에는
가을로 가득 차 있습니다`
  }
];

const App: React.FC = () => {
  const [selectedPoemIndex, setSelectedPoemIndex] = useState(0);
  const [userInput, setUserInput] = useState('');
  const [accuracy, setAccuracy] = useState(100);

  const currentPoem = poems[selectedPoemIndex];
  const formattedPoem = `${currentPoem.title}\n${currentPoem.author}\n\n${currentPoem.content}`;

  const calculateAccuracy = (input: string, original: string) => {
    if (input.length === 0) return 100;
    let correct = 0;
    const minLength = Math.min(input.length, original.length);

    for (let i = 0; i < minLength; i++) {
      if (input[i] === original[i]) correct++;
    }

    return Math.floor((correct / input.length) * 100);
  };

  const handleTyping = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    const newInput = e.target.value;
    setUserInput(newInput);
    setAccuracy(calculateAccuracy(newInput, formattedPoem));
  };

  const handlePoemChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    setSelectedPoemIndex(Number(e.target.value));
    setUserInput('');
    setAccuracy(100);
  };

  const renderHighlightedText = () => {
    return formattedPoem.split('').map((char, index) => (
      <HighlightedText
        key={index}
        isCorrect={index >= userInput.length || char === userInput[index]}
      >
        {char}
      </HighlightedText>
    ));
  };

  return (
    <AppContainer>
      <Title>시 타이핑 연습</Title>
      <PoemSelector value={selectedPoemIndex} onChange={handlePoemChange}>
        {poems.map((poem, index) => (
          <option key={index} value={index}>
            {poem.title} - {poem.author}
          </option>
        ))}
      </PoemSelector>
      <TypingArea>
        <PoemDisplay>{renderHighlightedText()}</PoemDisplay>
        <TypingInput
          value={userInput}
          onChange={handleTyping}
          placeholder="시를 따라 입력해보세요..."
        />
        <AccuracyDisplay>정확도: {accuracy}%</AccuracyDisplay>
      </TypingArea>
    </AppContainer>
  );
};

export default App;
