import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';

const AppContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem;
  text-align: center;
  background-color: #2b2b2b;
  min-height: 100vh;
  color: #fff;
`;

const Title = styled.h1`
  color: #fff;
  margin-bottom: 2rem;
`;

const TypingArea = styled.div`
  margin-bottom: 2rem;
  position: relative;
  text-align: left;
`;

const TextContainer = styled.div`
  position: relative;
  font-size: 1.2rem;
  line-height: 1.8;
  font-family: monospace;
`;

const BaseText = styled.div`
  white-space: pre-wrap;
  color: #666;
  position: relative;
  z-index: 1;
  min-height: 200px;
  font-family: monospace;
  line-height: 1.8em;
`;

const OverlayText = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  white-space: pre-wrap;
  z-index: 2;
  width: 100%;
  font-family: monospace;
  line-height: 1.8em;
`;

const Char = styled.span<{ status: 'correct' | 'incorrect' | 'waiting' }>`
  color: ${props => {
    switch (props.status) {
      case 'correct': return '#fff';
      case 'incorrect': return '#ff4444';
      case 'waiting': return '#666';
    }
  }};
`;

const ProgressBar = styled.div`
  width: 100%;
  height: 4px;
  background-color: #444;
  margin-bottom: 1rem;
  border-radius: 2px;
`;

const Progress = styled.div<{ width: number }>`
  width: ${props => props.width}%;
  height: 100%;
  background-color: ${props => props.width === 100 ? '#4CAF50' : '#666'};
  border-radius: 2px;
  transition: width 0.3s ease;
`;

const CompletionMessage = styled.div<{ show: boolean }>`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background-color: rgba(0, 0, 0, 0.9);
  padding: 2rem;
  border-radius: 8px;
  display: ${props => props.show ? 'block' : 'none'};
  z-index: 100;
  text-align: center;
`;

const HiddenInput = styled.input`
  position: absolute;
  opacity: 0;
  pointer-events: auto;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  z-index: 3;
  cursor: text;
`;

const Cursor = styled.span`
  display: inline-block;
  width: 2px;
  height: 1.4em;
  background-color: #fff;
  margin-left: -1px;
  margin-right: 1px;
  vertical-align: middle;
  animation: blink 1s step-end infinite;
  position: relative;

  @keyframes blink {
    from, to {
      opacity: 1;
    }
    50% {
      opacity: 0;
    }
  }
`;

const poems = [
  {
    title: 'ëŒ€í™”',
    content: `ì‚¬ë‘ì„ ìƒê³  ë‚˜ëŠ” ì“°ë„¤
ì˜ ìˆê±°ë¼, ì§§ì•˜ë˜ ë°¤ë“¤ì•„
ì°½ë°–ì„ ë– ëŒë˜ ê²¨ìš¸ ì•ˆê°œë“¤ì•„
ì•„ë¬´ê²ƒë„ ëª¨ë¥´ë˜ ì´›ë¶ˆë“¤ì•„, ì˜ ìˆê±°ë¼
ê³µí¬ë¥¼ ê¸°ë‹¤ë¦¬ë˜ í° ì¢…ì´ë“¤ì•„
ë§ì„¤ì„ì„ ëŒ€ì‹ í•˜ë˜ ëˆˆë¬¼ë“¤ì•„
ì˜ ìˆê±°ë¼, ë” ì´ìƒ ë‚´ ê²ƒì´ ì•„ë‹Œ ì—´ë§ë“¤ì•„
ì¥ë‹˜ì²˜ëŸ¼ ë‚˜ ì´ì œ ë”ë“¬ê±°ë¦¬ë©° ë¬¸ì„ ì ê·¸ë„¤
ê°€ì—¾ì€ ë‚´ ì‚¬ë‘ ë¹ˆì§‘ì— ê°‡í˜”ë„¤`,
  }
];

// í•œê¸€ ìœ ë‹ˆì½”ë“œ ë²”ìœ„ ì²´í¬
const isKorean = (char: string) => {
  const code = char.charCodeAt(0);
  return code >= 0xAC00 && code <= 0xD7A3;
};

// ì´ˆì„± ê²€ì‚¬
const isChoseong = (char: string) => {
  const code = char.charCodeAt(0);
  return code >= 0x3131 && code <= 0x314E;
};

// ì´ˆì„± í…Œì´ë¸”
const CHOSEONG = ['ã„±', 'ã„²', 'ã„´', 'ã„·', 'ã„¸', 'ã„¹', 'ã…', 'ã…‚', 'ã…ƒ', 'ã……', 'ã…†', 'ã…‡', 'ã…ˆ', 'ã…‰', 'ã…Š', 'ã…‹', 'ã…Œ', 'ã…', 'ã…'];

// í•œê¸€ ìëª¨ ë¶„ë¦¬
const decompose = (char: string) => {
  const code = char.charCodeAt(0) - 0xAC00;
  if (code < 0 || code > 11171) return null;

  const jong = code % 28;
  const jung = ((code - jong) / 28) % 21;
  const cho = ((code - jong) / 28 - jung) / 21;

  return {
    cho: Math.floor(cho),
    jung: Math.floor(jung),
    jong: Math.floor(jong)
  };
};

// í˜„ì¬ ì…ë ¥ì´ ëª©í‘œì˜ ì¼ë¶€ì¸ì§€ í™•ì¸ (ë¡œì§ ê°œì„ )
const isPartialMatch = (target: string, input: string) => {
  // í•œê¸€ì´ ì•„ë‹ˆë©´ ì •í™•íˆ ì¼ì¹˜í•´ì•¼ í•¨
  if (!isKorean(target)) return target === input;

  // ì™„ì „íˆ ì¼ì¹˜í•˜ëŠ” ê²½ìš°
  if (target === input) return true;

  // ì´ˆì„±ë§Œ ì…ë ¥ëœ ê²½ìš° (ì˜ˆ: ëª©í‘œê°€ 'ì‚¬', ì…ë ¥ì´ 'ã……')
  if (isChoseong(input)) {
    const targetDecomp = decompose(target);
    if (targetDecomp) {
      return CHOSEONG[targetDecomp.cho] === input;
    }
  }

  // ì¤‘ì„±ë§Œ ì…ë ¥ëœ ê²½ìš° (ì¤‘ì„± ê²€ì‚¬ í•¨ìˆ˜ ì§ì ‘ ì‚¬ìš©)
  const isJungseong = (char: string) => {
    const code = char.charCodeAt(0);
    return code >= 0x314F && code <= 0x3163;
  };

  if (isJungseong(input)) {
    return false; // ì¤‘ì„±ë§Œ ìˆëŠ” ê²½ìš°ëŠ” ì¼ì¹˜í•˜ì§€ ì•ŠìŒ
  }

  // ì¢…ì„±ê¹Œì§€ ì…ë ¥ëœ ê²½ìš° (ì˜ˆ: ëª©í‘œê°€ 'ë¼', ì…ë ¥ì´ 'ë„')
  if (input.length === 1 && isKorean(input)) {
    const inputDecomp = decompose(input);
    const targetDecomp = decompose(target);

    if (inputDecomp && targetDecomp) {
      // ì´ˆì„±ê³¼ ì¤‘ì„±ì´ ì¼ì¹˜í•˜ë©´ ë¶€ë¶„ ì¼ì¹˜ë¡œ ê°„ì£¼
      return inputDecomp.cho === targetDecomp.cho && 
             inputDecomp.jung === targetDecomp.jung;
    }
  }

  return false;
};

const App: React.FC = () => {
  const [userInput, setUserInput] = useState('');
  const [completed, setCompleted] = useState(false);
  const [composing, setComposing] = useState(false);
  const [currentText, setCurrentText] = useState('');
  const [debug, setDebug] = useState({ event: '', data: '' });
  const [currentPosition, setCurrentPosition] = useState(0);
  const [currentLine, setCurrentLine] = useState(0);
  const inputRef = useRef<HTMLInputElement>(null);
  const currentPoem = poems[0];

  // ì‹œë¥¼ ì¤„ ë‹¨ìœ„ë¡œ ë¶„í• 
  const poemLines = currentPoem.content.split('\n');

  const calculateProgress = () => {
    if (userInput.length === 0) return 0;
    const correctChars = userInput.split('').filter((char, i) => char === currentPoem.content[i]).length;
    return (correctChars / currentPoem.content.length) * 100;
  };

  // í˜„ì¬ ì¤„ì´ ì™„ì„±ë˜ì—ˆëŠ”ì§€ í™•ì¸í•˜ëŠ” í•¨ìˆ˜ (ë¡œê·¸ ì™„ì „ ì œê±°)
  const checkLineCompletion = () => {
    // ìë™ ì¤„ë°”ê¿ˆ ë¹„í™œì„±í™”
    return;
  };

  // ë‹¤ìŒ ì¤„ë¡œ ì´ë™í•˜ëŠ” í•¨ìˆ˜ (ë¡œê·¸ ì œê±° ë° ìµœì í™”)
  const moveToNextLine = () => {
    // ë§ˆì§€ë§‰ ì¤„ì´ë©´ ì²˜ë¦¬í•˜ì§€ ì•ŠìŒ
    if (currentLine >= poemLines.length - 1) return;

    // ê°™ì€ ì¤„ì„ ë‹¤ì‹œ ì²˜ë¦¬í•˜ì§€ ì•Šë„ë¡ í–‰ ë²ˆí˜¸ ì¦ê°€
    const nextLine = currentLine + 1;

    // ë‹¤ìŒ ì¤„ì˜ ì‹œì‘ ìœ„ì¹˜ ê³„ì‚°
    let nextLineStartPosition = 0;
    for (let i = 0; i < nextLine; i++) {
      nextLineStartPosition += poemLines[i].length;
      if (i < nextLine - 1) nextLineStartPosition += 1; // ì´ì „ ì¤„ë“¤ì˜ ì¤„ë°”ê¿ˆ
    }

    // ì¤„ë°”ê¿ˆì´ ì´ë¯¸ ìˆëŠ”ì§€ í™•ì¸ (ì¤‘ë³µ ì¶”ê°€ ë°©ì§€)
    let updatedInput = userInput;
    const expectedBreaks = currentLine;
    const actualBreaks = (userInput.match(/\n/g) || []).length;

    if (actualBreaks < expectedBreaks + 1) {
      // ì¤„ë°”ê¿ˆì´ ì—†ìœ¼ë©´ ì¶”ê°€ (ì¤‘ë³µ ë°©ì§€)
      if (!updatedInput.endsWith('\n')) {
        updatedInput = userInput + '\n';
        setUserInput(updatedInput);
        setCurrentText(updatedInput);
      }
    }

    // ìƒíƒœ ì—…ë°ì´íŠ¸ - ë‹¤ìŒ ì¤„ë¡œ ì´ë™
    setCurrentLine(nextLine);

    // í˜„ì¬ ì…ë ¥ ìœ„ì¹˜ ì—…ë°ì´íŠ¸ (+1ì€ ì¤„ë°”ê¿ˆ)
    const newPosition = nextLineStartPosition + (nextLine > 0 ? 1 : 0);
    setCurrentPosition(newPosition);

    // í¬ì»¤ìŠ¤ ìœ ì§€
    if (inputRef.current) {
      inputRef.current.focus();
    }
  };

  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.focus();
    }
  }, []);

  // useEffect ì˜ì¡´ì„± ë°°ì—´ ìˆ˜ì •
  useEffect(() => {
    // ì¡°í•© ì¤‘ì¼ ë•ŒëŠ” ì²´í¬í•˜ì§€ ì•ŠìŒ
    if (!composing) {
      checkLineCompletion();
    }
  }, [userInput, composing, currentLine, poemLines]); // checkLineCompletion ì œê±°

  // ì…ë ¥ ì²˜ë¦¬ í•¨ìˆ˜
  const handleInput = (e: React.FormEvent<HTMLInputElement>) => {
    e.preventDefault();

    const value = e.currentTarget?.value || '';
    const selectionStart = e.currentTarget?.selectionStart ?? 0;

    setDebug({
      event: 'input',
      data: value + ' / cursor: ' + selectionStart + ' / composing: ' + composing
    });

    // í˜„ì¬ í…ìŠ¤íŠ¸ ì—…ë°ì´íŠ¸
    setCurrentText(value);

    // ì¡°í•© ì¤‘ì´ ì•„ë‹ ë•Œë§Œ ì‚¬ìš©ì ì…ë ¥ ë° ì»¤ì„œ ìœ„ì¹˜ ì—…ë°ì´íŠ¸
    if (!composing) {
      setUserInput(value);
      setCurrentPosition(selectionStart); // ì‹¤ì œ ì»¤ì„œ ìœ„ì¹˜ ë°˜ì˜

      // íƒ€ì´í•‘ ì™„ë£Œ ì²´í¬
      if (value === currentPoem.content) {
        setCompleted(true);
      }
    } else {
      // ì¡°í•© ì¤‘ì—ëŠ” ì»¤ì„œ ìœ„ì¹˜ë§Œ ì—…ë°ì´íŠ¸ (ê¸€ì ë’¤ì— í‘œì‹œí•˜ê¸° ìœ„í•´)
      setCurrentPosition(userInput.length);
    }
  };

  // IME ì¡°í•©ì´ ì‹œì‘ë  ë•Œ í˜¸ì¶œë©ë‹ˆë‹¤
  const handleCompositionStart = (e: React.CompositionEvent<HTMLInputElement>) => {
    setComposing(true);
    setDebug({ 
      event: 'composition start', 
      data: e.data || '' + ' / value: ' + e.currentTarget.value 
    });
  };

  // IME ì¡°í•©ì´ ì§„í–‰ ì¤‘ì¼ ë•Œ í˜¸ì¶œë©ë‹ˆë‹¤
  const handleCompositionUpdate = (e: React.CompositionEvent<HTMLInputElement>) => {
    const value = e.currentTarget.value;
    setCurrentText(value);
    setDebug({ 
      event: 'composition update', 
      data: e.data || '' + ' / value: ' + value
    });
  };

  // IME ì¡°í•©ì´ ì™„ë£Œë  ë•Œ í˜¸ì¶œë©ë‹ˆë‹¤
  const handleCompositionEnd = (e: React.CompositionEvent<HTMLInputElement>) => {
    const finalValue = e.currentTarget.value;
    setComposing(false);
    setCurrentText(finalValue);
    setUserInput(finalValue);

    // ì»¤ì„œ ìœ„ì¹˜ ì„¤ì • - ì„ íƒ ìœ„ì¹˜ê°€ ì•„ë‹Œ ì…ë ¥ ê¸¸ì´ë¥¼ ì‚¬ìš©
    const cursorPos = e.currentTarget.selectionStart || finalValue.length;
    setCurrentPosition(cursorPos);

    setDebug({ 
      event: 'composition end', 
      data: e.data || '' + ' / value: ' + finalValue + ' / cursor: ' + cursorPos
    });
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    // ë°±ìŠ¤í˜ì´ìŠ¤ ì²˜ë¦¬
    if (e.key === 'Backspace' && !composing) {
      e.preventDefault();
      const newInput = currentText.slice(0, -1);
      setCurrentText(newInput);
      setUserInput(newInput);
      // ì‚­ì œ í›„ ì»¤ì„œ ìœ„ì¹˜ ì—…ë°ì´íŠ¸
      setCurrentPosition(newInput.length);
    }

    // Enter í‚¤ ì²˜ë¦¬
    if (e.key === 'Enter' && !composing) {
      e.preventDefault();

      // í˜„ì¬ í…ìŠ¤íŠ¸ì˜ ë‹¤ìŒ ë¬¸ìê°€ ì¤„ë°”ê¿ˆì´ì–´ì•¼ í•˜ëŠ”ì§€ í™•ì¸
      const nextChar = currentPosition < currentPoem.content.length ? currentPoem.content[currentPosition] : '';

      if (nextChar === '\n') {
        // ì •í™•íˆ ì¤„ë°”ê¿ˆ ìœ„ì¹˜ì—ì„œ ì—”í„°í‚¤ë¥¼ ëˆ„ë¥¸ ê²½ìš°
        const newInput = currentText + '\n';
        setCurrentText(newInput);
        setUserInput(newInput);
        // ì¤„ë°”ê¿ˆ ë¬¸ì ë‹¤ìŒ ìœ„ì¹˜ë¡œ ì»¤ì„œ ì´ë™
        setCurrentPosition(newInput.length);
      }
    }
  };

  // ë Œë”ë§ í•¨ìˆ˜ ìˆ˜ì •
  const renderOverlayText = () => {
    const overlayContent = [];
    const targetChars = currentPoem.content.split('');
    const inputChars = currentText.split('');

    let skipNextNewline = false;

    // ê¸°ì¡´ì˜ ê° ë¬¸ìë³„ ì²˜ë¦¬ ë°©ì‹ ëŒ€ì‹ , ì›ë¬¸ í…ìŠ¤íŠ¸ ìœ„ì— ì…ë ¥ í…ìŠ¤íŠ¸ë¥¼ ê·¸ëŒ€ë¡œ í‘œì‹œ
    for (let i = 0; i < targetChars.length; i++) {
      const targetChar = targetChars[i];
      const inputChar = i < inputChars.length ? inputChars[i] : '';
      const showCursor = i === currentPosition;

      // ì¤„ë°”ê¿ˆ ë¬¸ì ì²˜ë¦¬
      if (targetChar === '\n') {
        if (skipNextNewline) {
          skipNextNewline = false;
          continue;
        }

        overlayContent.push(
          <span key={i} style={{ position: 'relative', display: 'block', height: '1.8em' }}>
            {i < inputChars.length && inputChars[i] === '\n' ? (
              <Char status="correct">
                <br />
              </Char>
            ) : null}
            {showCursor && <Cursor />}
          </span>
        );
        continue;
      }

      // ì…ë ¥ëœ ê¸€ì ì²˜ë¦¬
      if (i < inputChars.length) {
        // ì¤„ë°”ê¿ˆ ì´í›„ì˜ ê¸€ì ì²˜ë¦¬ (ì¤„ë°”ê¿ˆì´ ë°œìƒí•œ ê²½ìš°)
        if (inputChar === '\n' && targetChar !== '\n') {
          // ì…ë ¥ì—ëŠ” ì¤„ë°”ê¿ˆì´ ìˆì§€ë§Œ íƒ€ê²Ÿì—ëŠ” ì—†ëŠ” ê²½ìš° - ì´ëŠ” ë¶ˆì¼ì¹˜
          overlayContent.push(
            <span key={i} style={{ position: 'relative', display: 'block', height: '1.8em' }}>
              <Char status="incorrect">
                <br />
              </Char>
              {showCursor && <Cursor />}
            </span>
          );
          continue;
        }

        let status: 'correct' | 'incorrect' = isPartialMatch(targetChar, inputChar) ? 'correct' : 'incorrect';

        overlayContent.push(
          <span key={i} style={{ position: 'relative' }}>
            <Char status={status}>
              {inputChar}
            </Char>
            {showCursor && <Cursor />}
          </span>
        );
      } 
      // ì•„ì§ ì…ë ¥ë˜ì§€ ì•Šì€ ê¸€ìëŠ” ì»¤ì„œë§Œ í‘œì‹œ
      else {
        overlayContent.push(
          <span key={i} style={{ position: 'relative' }}>
            {showCursor && <Cursor />}
          </span>
        );
      }
    }

    return overlayContent;
  };

  const progress = calculateProgress();

  // ì…ë ¥ í•„ë“œì— í¬ì»¤ìŠ¤ ìœ ì§€ë¥¼ ìœ„í•œ íš¨ê³¼ ì¶”ê°€
  useEffect(() => {
    const focusInterval = setInterval(() => {
      if (inputRef.current && document.activeElement !== inputRef.current) {
        inputRef.current.focus();
      }
    }, 500);

    return () => clearInterval(focusInterval);
  }, []);

  // ì´ˆê¸°í™” ì‹œ ì»¤ì„œ ìœ„ì¹˜ë¥¼ 0ìœ¼ë¡œ ì„¤ì •
  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.focus();
      setCurrentPosition(0);
    }
  }, []); // ì˜ì¡´ì„± ë°°ì—´ ë¹„ì›Œë‘ 

  useEffect(() => {
    checkLineCompletion();
  }, [currentLine, currentText, checkLineCompletion]);

  // ì¤„ë°”ê¿ˆ ì²˜ë¦¬ë¥¼ ìœ„í•œ í•¨ìˆ˜ ì¶”ê°€
  const renderOverlayLine = (targetLine: string, lineIndex: number) => {
    // í˜„ì¬ ì¤„ì˜ ì‹œì‘ ìœ„ì¹˜ ê³„ì‚°
    let lineStartPos = 0;
    for (let i = 0; i < lineIndex; i++) {
      lineStartPos += poemLines[i].length + 1; // +1 for newline
    }

    // ì…ë ¥ëœ í…ìŠ¤íŠ¸ì—ì„œ í˜„ì¬ ì¤„ì— í•´ë‹¹í•˜ëŠ” ë¶€ë¶„ ì¶”ì¶œ
    let inputContent = '';
    if (currentText.length > lineStartPos) {
      const nextNewlinePos = currentText.indexOf('\n', lineStartPos);
      if (nextNewlinePos > -1) {
        // ë‹¤ìŒ ì¤„ë°”ê¿ˆì´ ìˆëŠ” ê²½ìš°
        inputContent = currentText.substring(lineStartPos, nextNewlinePos);
      } else {
        // ë‹¤ìŒ ì¤„ë°”ê¿ˆì´ ì—†ëŠ” ê²½ìš° (ë§ˆì§€ë§‰ ì¤„)
        inputContent = currentText.substring(lineStartPos);
      }
    }

    // ì¤„ ë‚´ ë¬¸ì ë‹¨ìœ„ ì²˜ë¦¬
    const result: JSX.Element[] = [];
    for (let i = 0; i < targetLine.length; i++) {
      const targetChar = targetLine[i];
      const inputChar = i < inputContent.length ? inputContent[i] : '';
      const globalCharPos = lineStartPos + i;
      const showCursor = globalCharPos === currentPosition;

      if (i < inputContent.length) {
        // ì…ë ¥ëœ ê¸€ì ì²˜ë¦¬
        const status = isPartialMatch(targetChar, inputChar) ? 'correct' : 'incorrect';
        result.push(
          <span key={i} style={{ position: 'relative' }}>
            <Char status={status}>{inputChar}</Char>
            {showCursor && <Cursor />}
          </span>
        );
      } else {
        // ì…ë ¥ë˜ì§€ ì•Šì€ ê¸€ìëŠ” ì»¤ì„œë§Œ í‘œì‹œ
        result.push(
          <span key={i} style={{ position: 'relative' }}>
            {showCursor && <Cursor />}
          </span>
        );
      }
    }

    // ì´ˆê³¼ ì…ë ¥ëœ ê¸€ìë“¤ ì²˜ë¦¬
    if (inputContent.length > targetLine.length) {
      for (let i = targetLine.length; i < inputContent.length; i++) {
        const globalCharPos = lineStartPos + i;
        const showCursor = globalCharPos === currentPosition;
        result.push(
          <span key={`excess-${i}`} style={{ position: 'relative' }}>
            <Char status="incorrect">{inputContent[i]}</Char>
            {showCursor && <Cursor />}
          </span>
        );
      }
    }

    return result;
  };

  // ë Œë”ë§ ë¶€ë¶„ ìˆ˜ì • - ê° ì¤„ì„ ê°œë³„ì ìœ¼ë¡œ ì²˜ë¦¬
  return (
    <AppContainer onClick={() => inputRef.current?.focus()}>
      <Title>ì‹œ íƒ€ì´í•‘ ì—°ìŠµ</Title>
      {/* ë””ë²„ê·¸ ì •ë³´ í‘œì‹œ (ê°œë°œ ëª¨ë“œì—ì„œë§Œ) */}
      {(process.env.NODE_ENV === 'development' || window.location.hostname === 'localhost') && (
        <div className="debug">
          <h3>ë””ë²„ê·¸ ì •ë³´:</h3>
          <pre>{JSON.stringify(debug, null, 2)}</pre>
        </div>
      )}
      <ProgressBar>
        <Progress width={progress} />
      </ProgressBar>
      <TypingArea>
        <TextContainer>
          {/* ì¤„ ë‹¨ìœ„ë¡œ ì²˜ë¦¬í•˜ì—¬ í…ìŠ¤íŠ¸ ì •ë ¬ ë¬¸ì œ í•´ê²° */}
          {poemLines.map((line, index) => (
            <div key={index} style={{ position: 'relative', height: '1.8em', marginBottom: '0.5em' }}>
              {/* ì›ë³¸ í…ìŠ¤íŠ¸ (íšŒìƒ‰) */}
              <div style={{ color: '#666', whiteSpace: 'pre-wrap' }}>{line}</div>

              {/* ì˜¤ë²„ë ˆì´ í…ìŠ¤íŠ¸ (ì…ë ¥ëœ í…ìŠ¤íŠ¸) */}
              <div 
                style={{ 
                  position: 'absolute', 
                  top: 0, 
                  left: 0, 
                  whiteSpace: 'pre-wrap',
                  zIndex: 2,
                  width: '100%'
                }}
              >
                {renderOverlayLine(line, index)}
              </div>
            </div>
          ))}
          <HiddenInput
            ref={inputRef}
            value={currentText}
            onChange={handleInput}
            onCompositionStart={handleCompositionStart}
            onCompositionUpdate={handleCompositionUpdate}
            onCompositionEnd={handleCompositionEnd}
            onKeyDown={handleKeyDown}
            autoFocus
            autoComplete="off"
            autoCorrect="off"
            spellCheck={false}
            aria-label="ì‹œ ì…ë ¥ í•„ë“œ"
          />
        </TextContainer>
      </TypingArea>
      <CompletionMessage show={completed}>
        <h2>ğŸ‰ ì¶•í•˜í•©ë‹ˆë‹¤! ğŸ‰</h2>
        <p>ì„±ê³µì ìœ¼ë¡œ ì‹œë¥¼ ì™„ì„±í•˜ì…¨ìŠµë‹ˆë‹¤!</p>
      </CompletionMessage>
    </AppContainer>
  );
};

export default App;
