const poems = [
  {
    title: '대화',
    content: `사랑을 잃고 나는 쓰네
잘 있거라, 짧았던 밤들아
창밖을 떠돌던 겨울 안개들아
아무것도 모르던 촛불들아, 잘 있거라
공포를 기다리던 흰 종이들아
말설임을 대신하던 눈물들아
잘 있거라, 더 이상 내 것이 아닌 열망들아
장님처럼 나 이제 더듬거리며 문을 잠그네
가엾은 내 사랑 빈집에 갇혔네`,
  }
];

// 한글 유니코드 범위 체크
const isKorean = (char: string) => {
  const code = char.charCodeAt(0);
  return code >= 0xAC00 && code <= 0xD7A3;
};

// 초성 검사
const isChoseong = (char: string) => {
  const code = char.charCodeAt(0);
  return code >= 0x3131 && code <= 0x314E;
};

// 중성 검사
const isJungseong = (char: string) => {
  const code = char.charCodeAt(0);
  return code >= 0x314F && code <= 0x3163;
};

// 초성 테이블
const CHOSEONG = ['ㄱ', 'ㄲ', 'ㄴ', 'ㄷ', 'ㄸ', 'ㄹ', 'ㅁ', 'ㅂ', 'ㅃ', 'ㅅ', 'ㅆ', 'ㅇ', 'ㅈ', 'ㅉ', 'ㅊ', 'ㅋ', 'ㅌ', 'ㅍ', 'ㅎ'];

// 한글 자모 분리
const decompose = (char: string) => {
  const code = char.charCodeAt(0) - 0xAC00;
  if (code < 0 || code > 11171) return null;

  const jong = code % 28;
  const jung = ((code - jong) / 28) % 21;
  const cho = ((code - jong) / 28 - jung) / 21;

  return {
    cho: Math.floor(cho),
    jung: Math.floor(jung),
    jong: Math.floor(jong)
  };
};

const App: React.FC = () => {
  // 상태 관리 단순화
  const [input, setInput] = useState('');
  const [composing, setComposing] = useState(false);
  const [cursorPosition, setCursorPosition] = useState(0);
  const [completed, setCompleted] = useState(false);
  const [currentLineIndex, setCurrentLineIndex] = useState(0); // 현재 타이핑 중인 줄 인덱스

  // 디버그 정보를 위한 타입 정의
  interface DebugInfo {
    showDebug: boolean;
    event?: string;
    data?: string;
    currentInput?: string;
    cursorPosition?: number;
    currentLineIndex?: number;
    isComposing?: boolean;
  }

  // 디버그 정보
  const [debug, setDebug] = useState<DebugInfo>({ 
    showDebug: true 
  });

  const inputRef = useRef<HTMLInputElement>(null);
  const currentPoem = poems[0];

  // 시를 줄 단위로 분할
  const poemLines = currentPoem.content.split('\n');

  // 진행 상황 계산
  const calculateProgress = () => {
    if (input.length === 0) return 0;
    const correctChars = input.split('').filter((char, i) => char === currentPoem.content[i]).length;
    return (correctChars / currentPoem.content.length) * 100;
  };

  // 입력 처리 단순화
  const handleInput = (e: React.ChangeEvent<HTMLInputElement>) => {
    const inputValue = e.target.value;
    setInput(inputValue);

    // 커서 위치 설정
    const cursorPos = e.target.selectionStart || 0;
    setCursorPosition(cursorPos);

    // 현재 줄 계산
    const lines = inputValue.substring(0, cursorPos).split('\n');
    const newLineIndex = lines.length - 1;
    setCurrentLineIndex(newLineIndex);

    // 디버그 정보 업데이트
    setDebug({
      showDebug: debug.showDebug,
      currentInput: inputValue,
      cursorPosition: cursorPos,
      currentLineIndex: newLineIndex,
      isComposing: composing
    });

    // 완료 여부 확인
    const isComplete = checkLineCompletion(inputValue);
    if (isComplete) {
      setCompleted(true);
    }
  };

  // 키 입력 처리: 엔터키 감지해서 다음 줄로 이동
  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      e.preventDefault(); // 기본 엔터 동작 방지

      const cursorPos = inputRef.current?.selectionStart || 0;
      const currentLines = input.split('\n');

      // 현재 줄 계산 (0부터 시작)
      const lines = input.substring(0, cursorPos).split('\n');
      const currentLineIdx = lines.length - 1;

      // 현재 줄의 텍스트
      const currentLineText = currentLines[currentLineIdx] || '';

      // 다음 줄이 있으면 다음 줄로 이동, 없으면 새 줄 추가
      if (currentLineIdx < poemLines.length - 1) {
        // 현재 줄까지의, 그리고 그 이후의 텍스트 추출
        const textBeforeCursor = input.substring(0, cursorPos);
        const textAfterCursor = input.substring(cursorPos);

        // 다음 줄의 시작 부분에 커서 위치 설정
        const newInput = textBeforeCursor + '\n' + textAfterCursor;
        const newCursorPos = cursorPos + 1; // 다음 줄의 시작 위치

        setInput(newInput);
        setCursorPosition(newCursorPos);
        setCurrentLineIndex(currentLineIdx + 1);

        // 입력창의 커서 위치 직접 설정
        setTimeout(() => {
          if (inputRef.current) {
            inputRef.current.selectionStart = newCursorPos;
            inputRef.current.selectionEnd = newCursorPos;
          }
        }, 0);
      }
    }
  };

  // 완료 여부 확인
  const checkLineCompletion = (value: string): boolean => {
    if (value.length > 0) {
      let allLinesMatch = true;
      const inputLines = value.split('\n');

      for (let i = 0; i < poemLines.length; i++) {
        if (i >= inputLines.length || inputLines[i] !== poemLines[i]) {
          allLinesMatch = false;
          break;
        }
      }

      return allLinesMatch && inputLines.length >= poemLines.length;
    }
    return false;
  };

  // 조합 이벤트 처리 단순화
  const handleCompositionStart = () => {
    setComposing(true);
    setDebug({
      ...debug,
      event: 'composition start',
      isComposing: true
    });
  };

  const handleCompositionUpdate = (e: React.CompositionEvent<HTMLInputElement>) => {
    setDebug({
      ...debug,
      event: 'composition update',
      data: e.data || ''
    });
  };

  const handleCompositionEnd = () => {
    setComposing(false);
    setDebug({
      ...debug,
      event: 'composition end',
      isComposing: false
    });
  };

  // 줄 단위 렌더링 간소화
  const renderOverlayLine = (targetLine: string, lineIndex: number) => {
    // 현재 텍스트를 줄 단위로 분리
    const textLines = input.split('\n');
    // 현재 줄에 해당하는 텍스트
    const inputContent = lineIndex < textLines.length ? textLines[lineIndex] : '';

    // 현재 줄의 시작 위치 계산
    let lineStartPos = 0;
    for (let i = 0; i < lineIndex; i++) {
      lineStartPos += (i < textLines.length ? textLines[i].length : 0) + 1; // +1 for newline
    }

    // 초기 상태 또는 빈 입력일 때 처리 (첫 줄의 첫 글자 앞에 커서)
    const isInitialState = input === '' && lineIndex === 0;

    // 현재 타이핑 중인 줄인지 확인
    const isActiveLine = lineIndex === currentLineIndex;

    // 커서가 현재 줄에 있는지 확인
    const isCursorInThisLine = isInitialState || 
                               (isActiveLine && cursorPosition >= lineStartPos && 
                                cursorPosition <= lineStartPos + inputContent.length);

    // 줄 내 커서 위치 (상대적 위치)
    const cursorPositionInLine = isInitialState ? 0 : (isCursorInThisLine ? (cursorPosition - lineStartPos) : -1);

    // 결과 렌더링
    const result: JSX.Element[] = [];
    let hasCursor = false;

    // 초기 상태에서 첫 글자 앞에 커서 배치
    if (isInitialState) {
      result.push(
        <span key="initial-cursor" style={{ position: 'relative' }}>
          <Cursor />
        </span>
      );
      hasCursor = true;
    }

    // 목표 텍스트 처리
    for (let i = 0; i < targetLine.length; i++) {
      const targetChar = targetLine[i];
      const isCursorHere = !isInitialState && isCursorInThisLine && cursorPositionInLine === i && !hasCursor;

      if (i < inputContent.length) {
        // 입력된 글자 처리
        const inputChar = inputContent[i];
        // 현재 입력 중인 줄이거나 이전 줄만 정확도 체크
        const status = lineIndex <= currentLineIndex ? 
                      (targetChar === inputChar ? 'correct' : 'incorrect') : 
                      'waiting';

        result.push(
          <span key={i} style={{ position: 'relative' }}>
            <Char status={composing && isActiveLine && i === inputContent.length - 1 ? 'composing' : status}>
              {inputChar}
            </Char>
            {isCursorHere && (hasCursor = true) && <Cursor />}
          </span>
        );
      } else {
        // 입력되지 않은 글자 처리
        result.push(
          <span key={i} style={{ position: 'relative' }}>
            <Char status="waiting">{targetChar}</Char>
            {isCursorHere && (hasCursor = true) && <Cursor />}
          </span>
        );
      }
    }

    // 초과 입력 처리
    if (inputContent.length > targetLine.length) {
      for (let i = targetLine.length; i < inputContent.length; i++) {
        const isCursorHere = isCursorInThisLine && cursorPositionInLine === i && !hasCursor;

        result.push(
          <span key={`excess-${i}`} style={{ position: 'relative' }}>
            <Char status={composing && isActiveLine && i === inputContent.length - 1 ? 'composing' : 'incorrect'}>
              {inputContent[i]}
            </Char>
            {isCursorHere && (hasCursor = true) && <Cursor />}
          </span>
        );
      }
    }

    // 끝에 커서 표시 (초기 상태가 아닐 때)
    if (!isInitialState && isCursorInThisLine && cursorPositionInLine === inputContent.length && !hasCursor) {
      result.push(
        <span key="end-cursor" style={{ position: 'relative' }}>
          <Cursor />
        </span>
      );
    }

    return result;
  };

  const progress = calculateProgress();

  // 초기화 시 포커스
  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.focus();
      setCursorPosition(0);
    }
  }, []);

  // 렌더링
  return (
    <AppContainer onClick={() => inputRef.current?.focus()}>
      <Title>시 타이핑 연습</Title>
      {/* 디버그 정보 표시 (개발 모드에서만) */}
      {(process.env.NODE_ENV === 'development' || window.location.hostname === 'localhost') && (
        <div className="debug">
          <h3>디버그 정보:</h3>
          <pre>{JSON.stringify(debug, null, 2)}</pre>
        </div>
      )}
      <ProgressBar>
        <Progress width={progress} />
      </ProgressBar>
      <TypingArea>
        <TextContainer>
          {/* 시 내용 표시 */}
          <BaseText>{currentPoem.content}</BaseText>

          {/* 입력 내용 오버레이 */}
          <OverlayText>
            {poemLines.map((line, i) => (
              <div key={i} style={{ whiteSpace: 'pre-wrap' }}>
                {renderOverlayLine(line, i)}
              </div>
            ))}
          </OverlayText>

          <HiddenInput
            ref={inputRef}
            value={input}
            onChange={handleInput}
            onKeyDown={handleKeyDown}
            onCompositionStart={handleCompositionStart}
            onCompositionUpdate={handleCompositionUpdate}
            onCompositionEnd={handleCompositionEnd}
            autoFocus
            autoComplete="off"
            autoCorrect="off"
            spellCheck={false}
            aria-label="시 입력 필드"
          />
        </TextContainer>
      </TypingArea>
      <CompletionMessage show={completed}>
        <h2>🎉 축하합니다! 🎉</h2>
        <p>성공적으로 시를 완성하셨습니다!</p>
      </CompletionMessage>
    </AppContainer>
  );
};

export default App;
