import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';

const AppContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem;
  text-align: center;
  background-color: #2b2b2b;
  min-height: 100vh;
  color: #fff;
`;

const Title = styled.h1`
  color: #fff;
  margin-bottom: 2rem;
`;

const TypingArea = styled.div`
  margin-bottom: 2rem;
  position: relative;
  text-align: left;
`;

const TextContainer = styled.div`
  position: relative;
  font-size: 1.2rem;
  line-height: 1.8;
  font-family: monospace;
`;

const BaseText = styled.div`
  white-space: pre-wrap;
  color: #666;
  position: relative;
  z-index: 1;
  min-height: 200px;
  font-family: monospace;
  line-height: 1.8em;
`;

const OverlayText = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  white-space: pre-wrap;
  z-index: 2;
  width: 100%;
  font-family: monospace;
  line-height: 1.8em;
`;

const Char = styled.span<{ status: 'correct' | 'incorrect' | 'waiting' }>`
  color: ${props => {
    switch (props.status) {
      case 'correct': return '#fff';
      case 'incorrect': return '#ff4444';
      case 'waiting': return '#666';
    }
  }};
`;

const ProgressBar = styled.div`
  width: 100%;
  height: 4px;
  background-color: #444;
  margin-bottom: 1rem;
  border-radius: 2px;
`;

const Progress = styled.div<{ width: number }>`
  width: ${props => props.width}%;
  height: 100%;
  background-color: ${props => props.width === 100 ? '#4CAF50' : '#666'};
  border-radius: 2px;
  transition: width 0.3s ease;
`;

const CompletionMessage = styled.div<{ show: boolean }>`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background-color: rgba(0, 0, 0, 0.9);
  padding: 2rem;
  border-radius: 8px;
  display: ${props => props.show ? 'block' : 'none'};
  z-index: 100;
  text-align: center;
`;

const HiddenInput = styled.input`
  position: absolute;
  opacity: 0;
  pointer-events: auto;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  z-index: 3;
  cursor: text;
`;

const Cursor = styled.span`
  display: inline-block;
  width: 2px;
  height: 1.4em;
  background-color: #fff;
  margin-left: -1px;
  margin-right: 1px;
  vertical-align: middle;
  animation: blink 1s step-end infinite;
  position: relative;

  @keyframes blink {
    from, to {
      opacity: 1;
    }
    50% {
      opacity: 0;
    }
  }
`;

const poems = [
  {
    title: '대화',
    content: `사랑을 잃고 나는 쓰네
잘 있거라, 짧았던 밤들아
창밖을 떠돌던 겨울 안개들아
아무것도 모르던 촛불들아, 잘 있거라
공포를 기다리던 흰 종이들아
말설임을 대신하던 눈물들아
잘 있거라, 더 이상 내 것이 아닌 열망들아
장님처럼 나 이제 더듬거리며 문을 잠그네
가엾은 내 사랑 빈집에 갇혔네`,
  }
];

// 한글 유니코드 범위 체크
const isKorean = (char: string) => {
  const code = char.charCodeAt(0);
  return code >= 0xAC00 && code <= 0xD7A3;
};

// 초성 검사
const isChoseong = (char: string) => {
  const code = char.charCodeAt(0);
  return code >= 0x3131 && code <= 0x314E;
};

// 초성 테이블
const CHOSEONG = ['ㄱ', 'ㄲ', 'ㄴ', 'ㄷ', 'ㄸ', 'ㄹ', 'ㅁ', 'ㅂ', 'ㅃ', 'ㅅ', 'ㅆ', 'ㅇ', 'ㅈ', 'ㅉ', 'ㅊ', 'ㅋ', 'ㅌ', 'ㅍ', 'ㅎ'];

// 한글 자모 분리
const decompose = (char: string) => {
  const code = char.charCodeAt(0) - 0xAC00;
  if (code < 0 || code > 11171) return null;

  const jong = code % 28;
  const jung = ((code - jong) / 28) % 21;
  const cho = ((code - jong) / 28 - jung) / 21;

  return {
    cho: Math.floor(cho),
    jung: Math.floor(jung),
    jong: Math.floor(jong)
  };
};

// 현재 입력이 목표의 일부인지 확인 (로직 개선)
const isPartialMatch = (target: string, input: string) => {
  // 한글이 아니면 정확히 일치해야 함
  if (!isKorean(target)) return target === input;

  // 완전히 일치하는 경우
  if (target === input) return true;

  // 초성만 입력된 경우 (예: 목표가 '사', 입력이 'ㅅ')
  if (isChoseong(input)) {
    const targetDecomp = decompose(target);
    if (targetDecomp) {
      return CHOSEONG[targetDecomp.cho] === input;
    }
  }

  // 중성만 입력된 경우 (중성 검사 함수 직접 사용)
  const isJungseong = (char: string) => {
    const code = char.charCodeAt(0);
    return code >= 0x314F && code <= 0x3163;
  };

  if (isJungseong(input)) {
    return false; // 중성만 있는 경우는 일치하지 않음
  }

  // 종성까지 입력된 경우 (예: 목표가 '라', 입력이 '랄')
  if (input.length === 1 && isKorean(input)) {
    const inputDecomp = decompose(input);
    const targetDecomp = decompose(target);

    if (inputDecomp && targetDecomp) {
      // 초성과 중성이 일치하면 부분 일치로 간주
      return inputDecomp.cho === targetDecomp.cho && 
             inputDecomp.jung === targetDecomp.jung;
    }
  }

  return false;
};

const App: React.FC = () => {
  const [userInput, setUserInput] = useState('');
  const [completed, setCompleted] = useState(false);
  const [composing, setComposing] = useState(false);
  const [currentText, setCurrentText] = useState('');
  const [debug, setDebug] = useState({ event: '', data: '' });
  const [currentPosition, setCurrentPosition] = useState(0);
  const [currentLine, setCurrentLine] = useState(0);
  const inputRef = useRef<HTMLInputElement>(null);
  const currentPoem = poems[0];

  // 시를 줄 단위로 분할
  const poemLines = currentPoem.content.split('\n');

  const calculateProgress = () => {
    if (userInput.length === 0) return 0;
    const correctChars = userInput.split('').filter((char, i) => char === currentPoem.content[i]).length;
    return (correctChars / currentPoem.content.length) * 100;
  };

  // 현재 줄이 완성되었는지 확인하는 함수 (로그 완전 제거)
  const checkLineCompletion = () => {
    // 자동 줄바꿈 비활성화
    return;
  };

  // 다음 줄로 이동하는 함수 (로그 제거 및 최적화)
  const moveToNextLine = () => {
    // 마지막 줄이면 처리하지 않음
    if (currentLine >= poemLines.length - 1) return;

    // 같은 줄을 다시 처리하지 않도록 행 번호 증가
    const nextLine = currentLine + 1;

    // 다음 줄의 시작 위치 계산
    let nextLineStartPosition = 0;
    for (let i = 0; i < nextLine; i++) {
      nextLineStartPosition += poemLines[i].length;
      if (i < nextLine - 1) nextLineStartPosition += 1; // 이전 줄들의 줄바꿈
    }

    // 줄바꿈이 이미 있는지 확인 (중복 추가 방지)
    let updatedInput = userInput;
    const expectedBreaks = currentLine;
    const actualBreaks = (userInput.match(/\n/g) || []).length;

    if (actualBreaks < expectedBreaks + 1) {
      // 줄바꿈이 없으면 추가 (중복 방지)
      if (!updatedInput.endsWith('\n')) {
        updatedInput = userInput + '\n';
        setUserInput(updatedInput);
        setCurrentText(updatedInput);
      }
    }

    // 상태 업데이트 - 다음 줄로 이동
    setCurrentLine(nextLine);

    // 현재 입력 위치 업데이트 (+1은 줄바꿈)
    const newPosition = nextLineStartPosition + (nextLine > 0 ? 1 : 0);
    setCurrentPosition(newPosition);

    // 포커스 유지
    if (inputRef.current) {
      inputRef.current.focus();
    }
  };

  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.focus();
    }
  }, []);

  // useEffect 의존성 배열 수정
  useEffect(() => {
    // 조합 중일 때는 체크하지 않음
    if (!composing) {
      checkLineCompletion();
    }
  }, [userInput, composing, currentLine, poemLines]); // checkLineCompletion 제거

  // 입력 처리 함수
  const handleInput = (e: React.FormEvent<HTMLInputElement>) => {
    e.preventDefault();

    const value = e.currentTarget?.value || '';
    const selectionStart = e.currentTarget?.selectionStart ?? 0;

    setDebug({
      event: 'input',
      data: value + ' / cursor: ' + selectionStart + ' / composing: ' + composing + ' / currentPosition: ' + currentPosition
    });

    // 현재 입력 위치가 어느 줄에 속하는지 확인
    let currentLineIndex = 0;
    let charsAccumulated = 0;

    // 현재 커서 위치에 기반한 줄 위치 찾기
    for (let i = 0; i < poemLines.length; i++) {
      const lineLength = poemLines[i].length;
      if (currentPosition >= charsAccumulated && currentPosition <= charsAccumulated + lineLength) {
        currentLineIndex = i;
        break;
      }
      charsAccumulated += lineLength + 1; // +1 for newline
    }

    // 현재 입력 텍스트를 줄 단위로 분리
    const textLines = currentText.split('\n');

    // 현재 줄을 입력 기반으로 업데이트
    if (composing) {
      // 조합 중에는 현재 텍스트만 업데이트하고 커서 위치 유지
      setCurrentText(value);
      return;
    }

    setCurrentText(value);
    setUserInput(value);
    setCurrentPosition(selectionStart);

    // 타이핑 완료 체크
    if (value === currentPoem.content) {
      setCompleted(true);
    }
  };

  // IME 조합이 시작될 때 호출됩니다
  const handleCompositionStart = (e: React.CompositionEvent<HTMLInputElement>) => {
    setComposing(true);
    setDebug({ 
      event: 'composition start', 
      data: e.data || '' + ' / value: ' + e.currentTarget.value 
    });
  };

  // IME 조합이 진행 중일 때 호출됩니다
  const handleCompositionUpdate = (e: React.CompositionEvent<HTMLInputElement>) => {
    const value = e.currentTarget.value;
    setCurrentText(value);
    setDebug({ 
      event: 'composition update', 
      data: e.data || '' + ' / value: ' + value
    });
  };

  // IME 조합이 완료될 때 호출됩니다
  const handleCompositionEnd = (e: React.CompositionEvent<HTMLInputElement>) => {
    const finalValue = e.currentTarget.value;
    setComposing(false);
    setCurrentText(finalValue);
    setUserInput(finalValue);

    // 커서 위치 설정 - 선택 위치가 아닌 입력 길이를 사용
    const cursorPos = e.currentTarget.selectionStart || finalValue.length;
    setCurrentPosition(cursorPos);

    setDebug({ 
      event: 'composition end', 
      data: e.data || '' + ' / value: ' + finalValue + ' / cursor: ' + cursorPos
    });
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    // 백스페이스 처리
    if (e.key === 'Backspace' && !composing) {
      e.preventDefault();
      const newInput = currentText.slice(0, -1);
      setCurrentText(newInput);
      setUserInput(newInput);
      // 삭제 후 커서 위치 업데이트
      setCurrentPosition(newInput.length);
    }

    // Enter 키 처리
    if (e.key === 'Enter' && !composing) {
      e.preventDefault();

      // 현재 텍스트를 줄 단위로 분리
      const textLines = currentText.split('\n');

      // 현재 커서가 어느 줄에 있는지 확인
      let currentLineIndex = 0;
      let charCount = 0;

      for (let i = 0; i < textLines.length; i++) {
        const lineLength = textLines[i].length;
        if (currentPosition >= charCount && currentPosition <= charCount + lineLength) {
          currentLineIndex = i;
          break;
        }
        // +1은 줄바꿈 문자
        charCount += lineLength + 1;
      }

      // 현재 줄이 원본 시의 해당 줄과 일치하는지 확인
      if (currentLineIndex < poemLines.length) {
        const originalLine = poemLines[currentLineIndex];
        const inputLine = textLines[currentLineIndex];

        // 현재 줄을 모두 입력했고 원본과 일치하는지 확인
        if (inputLine === originalLine) {
          // 다음 줄로 넘어갈 수 있는 경우
          if (currentLineIndex < poemLines.length - 1) {
            // 줄바꿈 추가
            const newText = [...textLines];
            // 빈 줄 추가 (다음 줄이 존재하지 않는 경우)
            if (newText.length <= currentLineIndex + 1) {
              newText.push('');
            }

            const newInput = newText.join('\n');

            setCurrentText(newInput);
            setUserInput(newInput);

            // 다음 줄 첫 글자 위치로 커서 이동
            let newPosition = 0;
            for (let i = 0; i <= currentLineIndex; i++) {
              newPosition += newText[i].length;
              if (i < newText.length - 1) {
                newPosition += 1; // 줄바꿈 문자 길이
              }
            }
            setCurrentPosition(newPosition);
          }
        }
      }
    }
  };

  // 렌더링 함수 수정
  const renderOverlayText = () => {
    const overlayContent = [];
    const targetChars = currentPoem.content.split('');
    const inputChars = currentText.split('');

    let skipNextNewline = false;

    // 기존의 각 문자별 처리 방식 대신, 원문 텍스트 위에 입력 텍스트를 그대로 표시
    for (let i = 0; i < targetChars.length; i++) {
      const targetChar = targetChars[i];
      const inputChar = i < inputChars.length ? inputChars[i] : '';
      const showCursor = i === currentPosition;

      // 줄바꿈 문자 처리
      if (targetChar === '\n') {
        if (skipNextNewline) {
          skipNextNewline = false;
          continue;
        }

        overlayContent.push(
          <span key={i} style={{ position: 'relative', display: 'block', height: '1.8em' }}>
            {i < inputChars.length && inputChars[i] === '\n' ? (
              <Char status="correct">
                <br />
              </Char>
            ) : null}
            {showCursor && <Cursor />}
          </span>
        );
        continue;
      }

      // 입력된 글자 처리
      if (i < inputChars.length) {
        // 줄바꿈 이후의 글자 처리 (줄바꿈이 발생한 경우)
        if (inputChar === '\n' && targetChar !== '\n') {
          // 입력에는 줄바꿈이 있지만 타겟에는 없는 경우 - 이는 불일치
          overlayContent.push(
            <span key={i} style={{ position: 'relative', display: 'block', height: '1.8em' }}>
              <Char status="incorrect">
                <br />
              </Char>
              {showCursor && <Cursor />}
            </span>
          );
          continue;
        }

        let status: 'correct' | 'incorrect' = isPartialMatch(targetChar, inputChar) ? 'correct' : 'incorrect';

        overlayContent.push(
          <span key={i} style={{ position: 'relative' }}>
            <Char status={status}>
              {inputChar}
            </Char>
            {showCursor && <Cursor />}
          </span>
        );
      } 
      // 아직 입력되지 않은 글자는 커서만 표시
      else {
        overlayContent.push(
          <span key={i} style={{ position: 'relative' }}>
            {showCursor && <Cursor />}
          </span>
        );
      }
    }

    return overlayContent;
  };

  const progress = calculateProgress();

  // 입력 필드에 포커스 유지를 위한 효과 추가
  useEffect(() => {
    const focusInterval = setInterval(() => {
      if (inputRef.current && document.activeElement !== inputRef.current) {
        inputRef.current.focus();
      }
    }, 500);

    return () => clearInterval(focusInterval);
  }, []);

  // 초기화 시 커서 위치를 0으로 설정
  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.focus();
      setCurrentPosition(0);
    }
  }, []); // 의존성 배열 비워둠

  useEffect(() => {
    checkLineCompletion();
  }, [currentLine, currentText, checkLineCompletion]);

  // 줄바꿈 처리를 위한 함수 추가
  const renderOverlayLine = (targetLine: string, lineIndex: number) => {
    // 현재 텍스트를 줄 단위로 분리
    const textLines = currentText.split('\n');
    // 현재 줄에 해당하는 텍스트 (해당 줄 인덱스가 없으면 빈 문자열)
    const inputContent = lineIndex < textLines.length ? textLines[lineIndex] : '';

    // 현재 줄의 시작 위치 계산 (전체 텍스트에서의 절대 위치)
    let lineStartPos = 0;
    for (let i = 0; i < lineIndex; i++) {
      lineStartPos += poemLines[i].length + 1; // +1 for newline
    }

    // 줄 내 문자 단위 처리
    const result: JSX.Element[] = [];
    for (let i = 0; i < targetLine.length; i++) {
      const targetChar = targetLine[i];
      const inputChar = i < inputContent.length ? inputContent[i] : '';
      const globalCharPos = lineStartPos + i;
      const showCursor = globalCharPos === currentPosition;

      if (i < inputContent.length) {
        // 입력된 글자 처리
        const status = isPartialMatch(targetChar, inputChar) ? 'correct' : 'incorrect';
        result.push(
          <span key={i} style={{ position: 'relative' }}>
            <Char status={status}>{inputChar}</Char>
            {showCursor && <Cursor />}
          </span>
        );
      } else {
        // 입력되지 않은 글자는 커서만 표시
        result.push(
          <span key={i} style={{ position: 'relative' }}>
            {showCursor && <Cursor />}
          </span>
        );
      }
    }

    // 초과 입력된 글자들 처리 (정확히 그 줄에 대해서만)
    if (inputContent.length > targetLine.length) {
      for (let i = targetLine.length; i < inputContent.length; i++) {
        const globalCharPos = lineStartPos + i;
        const showCursor = globalCharPos === currentPosition;
        result.push(
          <span key={`excess-${i}`} style={{ position: 'relative' }}>
            <Char status="incorrect">{inputContent[i]}</Char>
            {showCursor && <Cursor />}
          </span>
        );
      }
    }

    return result;
  };

  // 렌더링 부분 수정 - 각 줄을 개별적으로 처리, 줄 간격 수정
  return (
    <AppContainer onClick={() => inputRef.current?.focus()}>
      <Title>시 타이핑 연습</Title>
      {/* 디버그 정보 표시 (개발 모드에서만) */}
      {(process.env.NODE_ENV === 'development' || window.location.hostname === 'localhost') && (
        <div className="debug">
          <h3>디버그 정보:</h3>
          <pre>{JSON.stringify(debug, null, 2)}</pre>
        </div>
      )}
      <ProgressBar>
        <Progress width={progress} />
      </ProgressBar>
      <TypingArea>
        <TextContainer>
          {/* 줄 단위로 처리하여 텍스트 정렬 문제 해결 */}
          {poemLines.map((line, index) => (
            <div 
              key={index} 
              style={{ 
                position: 'relative', 
                height: '1.8em', 
                marginBottom: '0.5em',
                clear: 'both'
              }}
            >
              {/* 원본 텍스트 (회색) */}
              <div style={{ 
                color: '#666', 
                whiteSpace: 'pre-wrap', 
                overflow: 'visible' 
              }}>
                {line}
              </div>

              {/* 오버레이 텍스트 (입력된 텍스트) */}
              <div 
                style={{ 
                  position: 'absolute', 
                  top: 0, 
                  left: 0, 
                  whiteSpace: 'pre-wrap',
                  zIndex: 2,
                  width: '100%',
                  overflow: 'visible'
                }}
              >
                {renderOverlayLine(line, index)}
              </div>
            </div>
          ))}
          <HiddenInput
            ref={inputRef}
            value={currentText}
            onChange={handleInput}
            onCompositionStart={handleCompositionStart}
            onCompositionUpdate={handleCompositionUpdate}
            onCompositionEnd={handleCompositionEnd}
            onKeyDown={handleKeyDown}
            autoFocus
            autoComplete="off"
            autoCorrect="off"
            spellCheck={false}
            aria-label="시 입력 필드"
          />
        </TextContainer>
      </TypingArea>
      <CompletionMessage show={completed}>
        <h2>🎉 축하합니다! 🎉</h2>
        <p>성공적으로 시를 완성하셨습니다!</p>
      </CompletionMessage>
    </AppContainer>
  );
};

export default App;
